DECLARE @DateBudget       datetime,
        @cntHaushalt      int,
        @cntUnterstuetzt  int,
        @FactorProRata    float

SELECT @DateBudget = dbo.fnDateSerial(Jahr, Monat, 1, 0)
FROM BgBudget WHERE BgBudgetID = @BgBudgetID

SELECT @cntHaushalt = Count(*) FROM BgFinanzplan_BaPerson
