SET NOCOUNT ON
BEGIN TRANSACTION
GO
DELETE FROM [BgPositionsart]
GO
SET IDENTITY_INSERT [BgPositionsart] ON
GO
DECLARE @valueHG money, @valueUE money    

SELECT    
@valueHG = 
(
	SELECT TOP 1 CONVERT(money, TMP.ValueVar) 
		+ (
			CONVERT(money, dbo.fnXConfig(''System\Sozialhilfe\SKOS2005\GBL_PerPerson'', @RefDate)) 
			* (KZ.HgGrundbedarf - CONVERT(int, Child)) 
		) 
	FROM dbo.fnXConfigChild(''System\Sozialhilfe\SKOS2005\GBL\'', @RefDate)  TMP  
	WHERE CONVERT(int, Child) <= KZ.HgGrundbedarf                
	ORDER BY CONVERT(int, Child) DESC
),      

@valueUE = CASE WHEN KZ.GBUseHgUeFactor = 1 
		THEN 
			(
				SELECT TOP 1 CONVERT(money, TMP.ValueVar) 
					+ (
						CONVERT(money, dbo.fnXConfig(''System\Sozialhilfe\SKOS2005\GBL_PerPerson'', @RefDate)) * 	
						(KZ.HgGrundbedarf - CONVERT(int, Child)) 
					)                       
				FROM dbo.fnXConfigChild(''System\Sozialhilfe\SKOS2005\GBL\'', @RefDate)  TMP                       
				WHERE CONVERT(int, Child) <= KZ.HgGrundbedarf                       
				ORDER BY CONVERT(int, Child) DESC
			) * GBHgUeFactor                 
		ELSE 
			(
				SELECT TOP 1 CONVERT(money, TMP.ValueVar) 
					+ (
						CONVERT(money, dbo.fnXConfig(''System\Sozialhilfe\SKOS2005\GBL_PerPerson'', @RefDate)) 
						* (KZ.UeGrundbedarf - CONVERT(int, Child)) 
					)                       
				FROM dbo.fnXConfigChild(''System\Sozialhilfe\SKOS2005\GBL\'', @RefDate)  TMP                       
				WHERE CONVERT(int, Child) <= KZ.UeGrundbedarf                       
				ORDER BY CONVERT(int, Child) DESC
			)
		END 
FROM dbo.fnWhKennzahlen(@BgFinanzplanID) KZ    

SELECT 
	$0.00 AS HG_MIN, 
	@valueHG AS HG_MAX, 
	@valueHG AS HG_DEF,    
	$0.00 AS UE_MIN, 
	ROUND(@valueUE,0) AS UE_MAX, 
	ROUND(@valueUE,0) AS UE_DEF', N'15.0413', NULL, NULL)
SELECT $0.00 AS PR_MIN, @value AS PR_MAX, $0.00 AS PR_DEF, $0.00 AS UE_MIN, @value AS UE_MAX, $0.00 AS UE_DEF', N'15.0411', NULL, NULL)

SELECT
  @valueHG = (SELECT TOP 1 CONVERT(money, TMP.ValueVar) + (CONVERT(money, dbo.fnXConfig(''System\Sozialhilfe\SKOS2005\GBL_PerPerson'', @RefDate)) * (KZ.HgGrundbedarf - CONVERT(int, Child)) )
              FROM dbo.fnXConfigChild(''System\Sozialhilfe\SKOS2005\GBL\'', @RefDate)  TMP
              WHERE CONVERT(int, Child) <= KZ.HgGrundbedarf
              ORDER BY CONVERT(int, Child) DESC),

  @valueUE = CASE WHEN KZ.GBUseHgUeFactor = 1
               THEN (SELECT TOP 1 CONVERT(money, TMP.ValueVar) + (CONVERT(money, dbo.fnXConfig(''System\Sozialhilfe\SKOS2005\GBL_PerPerson'', @RefDate)) * (KZ.HgGrundbedarf - CONVERT(int, Child)) )
                     FROM dbo.fnXConfigChild(''System\Sozialhilfe\SKOS2005\GBL\'', @RefDate)  TMP
                     WHERE CONVERT(int, Child) <= KZ.HgGrundbedarf
                     ORDER BY CONVERT(int, Child) DESC) * GBHgUeFactor
               ELSE (SELECT TOP 1 CONVERT(money, TMP.ValueVar) + (CONVERT(money, dbo.fnXConfig(''System\Sozialhilfe\SKOS2005\GBL_PerPerson'', @RefDate)) * (KZ.UeGrundbedarf - CONVERT(int, Child)) )
                     FROM dbo.fnXConfigChild(''System\Sozialhilfe\SKOS2005\GBL\'', @RefDate)  TMP
                     WHERE CONVERT(int, Child) <= KZ.UeGrundbedarf
                     ORDER BY CONVERT(int, Child) DESC)
             END
FROM dbo.fnWhKennzahlen(@BgFinanzplanID) KZ

SELECT $0.00 AS HG_MIN, @valueHG AS HG_MAX, @valueHG AS HG_DEF,
  $0.00 AS UE_MIN, @valueUE AS UE_MAX, @valueUE AS UE_DEF', N'15.0413', NULL, NULL)
GO
GO
SET IDENTITY_INSERT [BgPositionsart] OFF
GO
COMMIT
GO