BEGIN
  DECLARE @SearchValue varchar(100)
  SET @SearchValue = {0}

  IF @SearchValue = ''.'' BEGIN
    --Suche im Klientensystem und in den KlientenInstitutionen

    SELECT
      ID$     = PRS.BaPersonID,
      Text$   = PRS.NameVorname,
      Typ     = ''Person'',
      Name    = PRS.NameVorname,
      Strasse = PRS.WohnsitzStrasseHausNr,
      Ort     = PRS.WohnsitzPLZOrt
    FROM vwPerson   PRS
    WHERE
      PRS.BaPersonID IN
       (SELECT BaPersonID_1 FROM BaPerson_Relation WHERE BaPersonID_2 = {1}
        UNION ALL
        SELECT BaPersonID_2 FROM BaPerson_Relation WHERE BaPersonID_1 = {1}
        UNION ALL
        SELECT {1})
    UNION ALL
    SELECT
      ID$     = -INS.BaInstitutionID,
      Text$   = INS.Name + IsNull('', '' + INS.Ort, ''''),
      Typ     = ''Institution'',
      Name    = INS.Name,
      Strasse = INS.StrasseHausNr,
      Ort     = INS.PLZOrt
    FROM vwInstitution                 INS
      INNER JOIN BaPerson_BaInstitution  DPO ON DPO.BaInstitutionID = INS.BaInstitutionID
    WHERE
      DPO.BaPersonID IN
       (SELECT BaPersonID_1 FROM BaPerson_Relation WHERE BaPersonID_2 = {1}
        UNION ALL
        SELECT BaPersonID_2 FROM BaPerson_Relation WHERE BaPersonID_1 = {1}
        UNION ALL
        SELECT {1})
      AND INS.Aktiv = 1
    ORDER BY Typ DESC, Name

  END ELSE BEGIN
    --Suche im ganzen Personenstamm und im ganzen Institutionenstamm

    SET @SearchValue = replace(@SearchValue, '' '', ''%'')
    SET @SearchValue = replace(@SearchValue, '','', ''%'')
    SET @SearchValue = @SearchValue + ''%''

    SELECT
      ID$     = PRS.BaPersonID,
      Text$   = PRS.NameVorname,
      Typ     = ''Person'',
      Name    = PRS.NameVorname,
      Strasse = PRS.WohnsitzStrasseHausNr,
      Ort     = PRS.WohnsitzPLZOrt
    FROM vwPerson   PRS
    WHERE PRS.NameVorname LIKE @SearchValue
    UNION ALL
    SELECT
      ID$     = -INS.BaInstitutionID,
      Text$   = INS.Name + IsNull('', '' + INS.Ort, ''''),
      Typ     = ''Institution'',
      Name    = INS.Name,
      Strasse = INS.StrasseHausNr,
      Ort     = INS.PLZOrt
    FROM vwInstitution  INS
    WHERE INS.Name + IsNull('', '' + INS.Ort, '''') LIKE @SearchValue
      AND INS.Aktiv = 1
    UNION ALL
    SELECT
      ID$     = -VPM.VmPriMaID - 100000,
      Text$   = VPM.Name + IsNull('', '' + VPM.Vorname, ''''),
      Typ     = ''PriMa'',
      Name    = VPM.Name + IsNull('', '' + VPM.Vorname, ''''),
      Strasse = ADR.Strasse + IsNull('' '' + ADR.HausNr, ''''),
      Ort     = IsNull(ADR.PLZ + '' '', '''') + ADR.Ort
    FROM VmPriMa           VPM
      LEFT JOIN BaAdresse  ADR ON ADR.VmPriMaID = VPM.VmPriMaID
    WHERE VPM.Name + IsNull('', '' + VPM.Vorname, '''') LIKE @SearchValue
      AND VPM.isActive = 1
    ORDER BY Name
  END
END
