-------------------------------------------------------------------------------
-- pre-steps
-------------------------------------------------------------------------------
SET NOCOUNT ON;
GO

UPDATE dbo.BaBeruf
  SET Beruf = 'sans profession',
      BezeichnungM = 'sans profession',
      BezeichnungW = 'sans profession'
WHERE BFSCode = 0
  AND SortKey = 0

UPDATE dbo.BaBeruf
  SET Beruf = 'sans profession (non déterminé)',
      BezeichnungM = 'sans profession (non déterminé)',
      BezeichnungW = 'sans profession (non déterminé)'
WHERE BFSCode = 0
  AND SortKey = 99999


-------------------------------------------------------------------------------
-- init vars and table
-------------------------------------------------------------------------------
DECLARE @EntriesCount INT;
DECLARE @EntriesIterator INT;

DECLARE @BaBerufID int;
DECLARE @Beruf varchar(200);
DECLARE @BezeichnungM varchar(200);
DECLARE @BezeichnungW varchar(200);
DECLARE @SortKey int;
DECLARE @BFSCode int;

DECLARE @MaxBaBerufID int;


DECLARE @BerufeToInsert TABLE
(
  BerufeToInsertID INT IDENTITY(1, 1) NOT NULL, -- ID für die Ausführung in diesem Script
  Beruf varchar(200) NULL,  
  BezeichnungM varchar(200) NOT NULL, 
  BezeichnungW varchar(200) NOT NULL,
  BFSCode INT  NOT NULL
);


-- Formel: =CONCATENATE("INSERT INTO @BerufeToInsert(BFSCode, BezeichnungM, BezeichnungW, Beruf) VALUES(";A2;", '";SUBSTITUTE(B2;"'";"''");"', '";SUBSTITUTE(C2;"'";"''");"', '";SUBSTITUTE(B2;"'";"''");" / ";SUBSTITUTE(C2;"'";"''");"')")
-- TODO select aus Excel hinzufügen


-- prepare vars for loop
SELECT @EntriesCount = COUNT(1) FROM @BerufeToInsert;  -- needs to be done just after filling!
SET @EntriesIterator = 1;        -- needs to start just at the same value as IDENTITY column on table
SELECT @MaxBaBerufID = MAX(BaBerufID) FROM BaBeruf WITH (READUNCOMMITTED) WHERE BaBerufID <> 99999

--Check if IDENTITY_INSERT is needed
DECLARE @IsIdentity INT;
SELECT @IsIdentity = (select columnproperty(object_id('dbo.BaBeruf'),'BaBerufID','IsIdentity'))
IF (@IsIdentity = 1)
  BEGIN
  SET IDENTITY_INSERT dbo.BaBeruf  ON 
END


-------------------------------------------------------------------------------
-- loop all entries
-------------------------------------------------------------------------------
WHILE (@EntriesIterator <= @EntriesCount)
BEGIN
  -- get current entry
  SELECT  @BFSCode = TMP.BFSCode,
          @Beruf = TMP.Beruf,
          @BezeichnungM = TMP.BezeichnungM,
          @BezeichnungW = TMP.BezeichnungW
  FROM @BerufeToInsert TMP
  WHERE TMP.BerufeToInsertID = @EntriesIterator;
  
  -----------------------------------------------------------------------------
  -- do something
  -----------------------------------------------------------------------------
  BEGIN TRY
    BEGIN TRAN;
    --@MaxBaBerufID inkrementieren
    SELECT @MaxBaBerufID =   @MaxBaBerufID + 1 
    
    --Insert oder Update machen  
    IF EXISTS (SELECT TOP 1 1 FROM BaBeruf WHERE BFSCode = @BFSCode)
    BEGIN
      UPDATE BaBeruf SET BezeichnungM = @BezeichnungM, BezeichnungW = @BezeichnungW, Beruf=@Beruf  WHERE BFSCode = @BFSCode 
      PRINT ('Bestehenden Eintrag für: ' + @Beruf + ' aktualisiert');
    END
    ELSE
    BEGIN
      VALUES (@MaxBaBerufID, @Beruf, @BezeichnungM, @BezeichnungW, @MaxBaBerufID,  @BFSCode)

      PRINT ('Neuer Eintrag erstellt für: ' + @Beruf);
    END
    COMMIT;
  END TRY
  BEGIN CATCH
    ROLLBACK;

    DECLARE @ErrorMessage VARCHAR(MAX);
    DECLARE @ErrorSeverity INT;
    DECLARE @ErrorState INT;

    SELECT @ErrorMessage = ERROR_MESSAGE(),
           @ErrorSeverity = ERROR_SEVERITY(),
           @ErrorState = ERROR_STATE();

    RAISERROR (@ErrorMessage, @ErrorSeverity, @ErrorState);
  END CATCH;
  -----------------------------------------------------------------------------
  
  -- prepare for next entry
  SET @EntriesIterator = @EntriesIterator + 1;
END;

-------------------------------------------------------------------------------
-- post-steps
-------------------------------------------------------------------------------

IF (@IsIdentity = 1)
BEGIN
SET IDENTITY_INSERT dbo.BaBeruf  OFF 
END

SET NOCOUNT OFF;
GO
