        /// </summary>
        protected override void Dispose(bool disposing)
        {
            if (disposing)
            {
                if ((components != null))
                {
                    components.Dispose();
                }
            }
            base.Dispose(disposing);
        }

        #endregion

        #region Protected Methods

        protected override void NewSearch()
        {
            base.NewSearch();
            dlgSearchUserID.Focus();
        }

        #endregion

        #region Private Methods

        private void CtlChangeLog_Load(object sender, System.EventArgs e)
        {
            // start new search
            NewSearch();

            // show first tab on default
            tabControlSearch.SelectedTabIndex = 0;
        }

        private void qryXChangeLog_AfterFill(object sender, System.EventArgs e)
        {
            qryXChangeLog.Last();
        }

        private void qryXChangeLog_AfterInsert(object sender, System.EventArgs e)
        {
            qryXChangeLog["DateTime"] = System.DateTime.Now.Date.ToShortDateString();
            qryXChangeLog["UserID"] = Session.User.UserID;
        }

        private void qryXChangeLog_BeforePost(object sender, System.EventArgs e)
        {
            // validate input values for must-fields
            DBUtil.CheckNotNullField( qryXChangeLog, "Subject", KissMsg.GetMLMessage( "CtlChangeLog", "SubjectField", "Thema / Maske" ), edtSubject );
            DBUtil.CheckNotNullField( qryXChangeLog, "Change", KissMsg.GetMLMessage( "CtlChangeLog", "ChangeField", "Änderungen" ), memChange );
            DBUtil.CheckNotNullField( qryXChangeLog, "DateTime", KissMsg.GetMLMessage( "CtlChangeLog", "DateTimeField", "Datum" ), datDateTime );
        }

        #endregion
    }
}