        private const string sql = @"
            SELECT
              PRS.BaPersonID,
              PRS.NameVorname,
              PRS.WohnsitzStrasseHausNr,
              PRS.WohnsitzPostfach,
              PRS.WohnsitzPLZOrt,
              PRS.WohnsitzLand,
              PRS.AHVNummer,
              PRS.Geburtsdatum,
              PRS.Nationalitaet,
              PRS.Heimatort,
              PRS.Bemerkung,
              NrNameVorname  = convert(varchar, PRS.BaPersonID) + ' ' + PRS.NameVorname,
              KST.KbKostenstelleID,
              Geschlecht     = GES.Text,
              Zivilstand     = ZIV.Text
            FROM vwPerson              PRS
              LEFT JOIN KbKostenstelle KST ON KST.BaPersonID = PRS.BaPersonID
              LEFT JOIN XLOVCode       GES ON GES.LOVName = 'Geschlecht' AND GES.Code = PRS.GeschlechtCode
              LEFT JOIN XLOVCode       ZIV ON ZIV.LOVName = 'Zivilstand' AND ZIV.Code = PRS.ZivilstandCode
            WHERE  PRS.BaPersonID = {0}";

        private const string sqlIstUnterstuetzt = @"
            SELECT TOP 1 1
	        FROM dbo.FaLeistung                  LEI_A WITH (READUNCOMMITTED)
	          INNER JOIN dbo.BgFinanzplan          FPL WITH (READUNCOMMITTED) ON LEI_A.FaLeistungID = FPL.FaLeistungID
	          INNER JOIN dbo.BgFinanzplan_BaPerson FPP WITH (READUNCOMMITTED) ON FPL.BgFinanzplanID = FPP.BgFinanzplanID
	          INNER JOIN dbo.BgBudget              BDG WITH (READUNCOMMITTED) ON FPL.BgFinanzplanID = BDG.BgFinanzplanID
	          WHERE LEI_A.Modulid = 3
	          AND FPP.IstUnterstuetzt = 1 
	          AND FPP.BaPersonID = {0}";

