        #region Public Methods

        public override void OnRefreshData()
        {
            base.OnRefreshData();
            qryPersonA.Fill(sql, BaPersonID_A);
            qryPersonB.Fill(sql, BaPersonID_B);

            if (qryPersonA.Count == 0) qryPersonA.Insert(null);
            if (qryPersonB.Count == 0) qryPersonB.Insert(null);

            KbKostenstelleID_A = 0;
            KbKostenstelleID_B = 0;
            if (qryPersonA["KbKostenstelleID"] is int)
            {
                KbKostenstelleID_A = (int)qryPersonA["KbKostenstelleID"];
            }
            if (qryPersonB["KbKostenstelleID"] is int)
            {
                KbKostenstelleID_B = (int)qryPersonB["KbKostenstelleID"];
            }

            btnMigrateA.Enabled = !DBUtil.IsEmpty(qryPersonA["BaPersonID"]) && !DBUtil.IsEmpty(qryPersonB["BaPersonID"]);
            btnMigrateB.Enabled = btnMigrateA.Enabled;
        }

