        #region Private Methods

        private void Migrate(int BaPersonID, int BaPersonID_Delete, int KbKostenstelleID, int KbKostenstelleID_Delete, string Person, string Person_Delete)
        {
            if (BaPersonID == 0 || BaPersonID_Delete == 0)
            {
                KissMsg.ShowInfo("CtlDoublePerson", "PersonABLeer", "Person A und Person B müssen ausgefüllt sein!");
                return;
            }

            if (BaPersonID == BaPersonID_Delete)
            {
                KissMsg.ShowInfo("CtlDoublePerson", "PersonABIdentisch", "Person A und Person B sind identisch (gleiche Nr)!");
                return;
            }

            qryIstUnterstuetzt = new SqlQuery();
            qryIstUnterstuetzt.Fill(sqlIstUnterstuetzt, BaPersonID_Delete);
            if (qryIstUnterstuetzt.Count > 0)
            {
                KissMsg.ShowInfo("CtlDoublePerson", "PersonIstUnterstuetzt", "Die zu löschende Person wurde in einem Finanzplan unterstützt. Das Löschen ist daher nicht möglich.");
                return;
            }

            if (!KissMsg.ShowQuestion("CtlDoublePerson", "StammdatenWerdenGeloescht", "Achtung: Die Stammdaten der {0} werden gelöscht und alle Falldaten der {1} zugeordnet.\r\n\r\nSoll diese Aktion durchgeführt werden?", 0, 0, true, Person_Delete, Person))
                return;

            ApplicationFacade.DoEvents();

            try
            {
                Session.BeginTransaction();

                DBUtil.NewHistoryVersion();

                lblAktion.Text = "Wohnsituation bereinigen";
                DBUtil.ExecSQLThrowingException(60, @"
                    DELETE ADR
                    FROM BaAdresse ADR
                    WHERE ADR.BaPersonID = {1}
                      AND EXISTS (SELECT * FROM BaAdresse
                                  WHERE BaPersonID = {0} AND AdresseCode = ADR.AdresseCode)"
                    , BaPersonID, BaPersonID_Delete);

                lblAktion.Text = "Beziehungen bereinigen";
                DBUtil.ExecSQLThrowingException(60, @"
                    DELETE DRE
                    FROM BaPerson_Relation  DRE
                    WHERE (DRE.BaPersonID_1 = {1}
                             AND EXISTS (SELECT * FROM BaPerson_Relation
                                         WHERE BaPersonID_1 = {0} AND BaPersonID_2 = DRE.BaPersonID_2))
                      OR  (DRE.BaPersonID_2 = {1}
                             AND EXISTS (SELECT * FROM BaPerson_Relation
                                         WHERE BaPersonID_2 = {0} AND BaPersonID_1 = DRE.BaPersonID_1))"
                    , BaPersonID, BaPersonID_Delete);

                lblAktion.Text = "BFS Werte löschen ...";
                ApplicationFacade.DoEvents();
                DBUtil.ExecSQLThrowingException(60, "DELETE XBFSWert WHERE BaPersonID = {0}", BaPersonID_Delete);

                // alle abhängigen Daten auf neue Person umhängen
                lblAktion.Text = "abhängige Daten (Falldaten) umhängen ...";
                ApplicationFacade.DoEvents();
                DBUtil.ExecSQLThrowingException(120, "EXECUTE spXRowMerge {0}, {1}, {2}", "BaPerson", BaPersonID, BaPersonID_Delete);

                DBUtil.ExecSQLThrowingException(120, "EXECUTE spXRowMerge {0}, {1}, {2}", "KbKostenstelle", KbKostenstelleID, KbKostenstelleID_Delete);

                Session.Commit();
            }
            catch (Exception ex)
            {
                Session.Rollback();
                KissMsg.ShowError("CtlDoublePerson", "FehlerDatenbereinigung", "Bei Versuch, die Datenbereinigung durchzuführen, ist ein Fehler aufgetreten", ex);
            }
            finally
            {
                lblAktion.Text = "";
            }
        }

        private void btnMigrateA_Click(object sender, System.EventArgs e)
        {
            Migrate(BaPersonID_B, BaPersonID_A, KbKostenstelleID_B, KbKostenstelleID_A, "Person B", "Person A");
            OnRefreshData();
        }

        private void btnMigrateB_Click(object sender, System.EventArgs e)
        {
            Migrate(BaPersonID_A, BaPersonID_B, KbKostenstelleID_A, KbKostenstelleID_B, "Person A", "Person B");
            OnRefreshData();
        }

        private void editPersonA_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            DlgAuswahl dlg = new DlgAuswahl();
            try
            {
                e.Cancel = !dlg.PersonSuchen(((KissButtonEdit)sender).Text, e.ButtonClicked);
                if (e.Cancel)
                    return;
                else if (sender == editPersonA)
                {
                    BaPersonID_A = 0;
                    BaPersonID_A = (int)dlg["BaPersonID"];
                }
                else if (sender == editPersonB)
                {
                    BaPersonID_B = 0;
                    BaPersonID_B = (int)dlg["BaPersonID"];
                }
            }
            catch { }
            OnRefreshData();
        }

