        #region Private Methods

        private void CtlFaUnterlagenlisteVorgabe_Load(object sender, System.EventArgs e)
        {
            //Holen der Filterwerte für die Unterlagen (F, FA)
            SqlQuery qry = DBUtil.OpenSQL(@"
                select Code = Value3, Text = Value3 from XLOVCode
                where LOVName = 'FaUnterlagenKategorieVorgabe'
                and Value3 is not null
                group by Value3");
            edtSucheVerwendung.LoadQuery(qry);
            edtSucheVerwendung.EditValue = (string)DBUtil.ExecuteScalarSQL(@"
                select top 1 Code = Value3, Text = Value3 from XLOVCode
                where LOVName = 'FaUnterlagenKategorieVorgabe'
                and Value3 is not null
                group by Value3");

            //colTabSeite.ColumnEdit = grdMain.GetLOVLookUpEdit((SqlQuery)edtTabSeite.Properties.DataSource);

            qryFaUnterlagenlisteVorgabe.Fill();
        }

        private void edtSucheVerwendung_EditValueChanged(object sender, System.EventArgs e)
        {
            edtTabSeite.LOVFilter = edtSucheVerwendung.Text;
            edtTabSeite.LOVName = "FaUnterlagenKategorieVorgabe";
            //KissMsg.ShowInfo("Aha "+ edtSucheVerwendung.Text);
        }

        private void qryFaUnterlagenlisteVorgabe_AfterInsert(object sender, System.EventArgs e)
        {
            qryFaUnterlagenlisteVorgabe["FaUnterlagenKategorieVorgabeCode"] = DBNull.Value;
            qryFaUnterlagenlisteVorgabe["Default_Notwendig"] = false;
            qryFaUnterlagenlisteVorgabe["HatZusatzfeld"] = false;

            edtTabSeite.Focus();
        }

        private void qryFaUnterlagenlisteVorgabe_BeforePost(object sender, System.EventArgs e)
        {
            DBUtil.CheckNotNullField(edtTabSeite,lblTabSeite.Text);
            DBUtil.CheckNotNullField(edtGruppe,lblGruppe.Text);
            DBUtil.CheckNotNullField(edtKurzname,lblKurzname.Text);
            DBUtil.CheckNotNullField(edtName,lblName.Text);
            DBUtil.CheckNotNullField(edtSortierung,lblSortierung.Text);
            qryFaUnterlagenlisteVorgabe["TabSeite"] = this.edtTabSeite.EditValue;
        }

