        #region Private Methods

        private void CtlKbZahlungslaufValuta_Load(object sender, System.EventArgs e)
        {
            SqlQuery qry = DBUtil.OpenSQL(@"
                SELECT Code = Jahr FROM KbZahlungslaufValuta
                UNION SELECT Year(GetDate())
                UNION SELECT Year(GetDate()) + 1
                UNION SELECT Year(GetDate()) + 2");

            ddlYear.Properties.DataSource = qry;
            ddlYear.EditValue = DateTime.Today.Year;

            FillData();
        }

        private void FillData()
        {
            DBUtil.ExecSQL(@"
            IF (SELECT COUNT(*) FROM KbZahlungslaufValuta WHERE Jahr = {0}) < 12 BEGIN
              INSERT INTO KbZahlungslaufValuta (Monat, Jahr)
            SELECT Monat, {0}
            FROM (SELECT Monat = 1
                  UNION ALL SELECT  2
                  UNION ALL SELECT  3
                  UNION ALL SELECT  4
                  UNION ALL SELECT  5
                  UNION ALL SELECT  6
                  UNION ALL SELECT  7
                  UNION ALL SELECT  8
                  UNION ALL SELECT  9
                  UNION ALL SELECT 10
                  UNION ALL SELECT 11
                  UNION ALL SELECT 12) TMP
            WHERE NOT EXISTS (SELECT * FROM KbZahlungslaufValuta WHERE Jahr = {0} AND Monat = TMP.Monat)
            END",
                ddlYear.EditValue);

            qryZahlungslauf.Fill(ddlYear.EditValue);
        }

        private void ddlYear_EditValueChanged(object sender, System.EventArgs e)
        {
            FillData();
        }

        #endregion
