        private void CtlTaskUserGroup_Load(object sender, System.EventArgs e)
        {
            qryFaPendenzgruppe.Fill();

            qryZugeteilt.DeleteQuestion = null;
        }

        private void DisplayMembers()
        {
            qryZugeteilt.Fill(qryFaPendenzgruppe["FaPendenzgruppeID"]);

            qryVerfuegbar.Fill(qryFaPendenzgruppe["FaPendenzgruppeID"]);
        }

        private void btnAddAll_Click(object sender, System.EventArgs e)
        {
            if (qryFaPendenzgruppe.Count == 0 || qryVerfuegbar.Count == 0) return;
            if (qryFaPendenzgruppe.Row.RowState == DataRowState.Added && !qryFaPendenzgruppe.Post()) return;

            DBUtil.ExecSQL(@"
                INSERT INTO FaPendenzgruppe_User (FaPendenzgruppeID, UserID)
                  SELECT {0}, UserID
                  FROM XUser   USR
                  WHERE NOT EXISTS (SELECT * FROM FAPendenzgruppe_User WHERE FaPendenzgruppeID = {0} AND UserID = USR.UserID)", qryFaPendenzgruppe["FaPendenzgruppeID"]);

            DisplayMembers();
        }

        private void btnAdd_Click(object sender, System.EventArgs e)
        {
            if (qryFaPendenzgruppe.Count == 0 || qryVerfuegbar.Count == 0) return;
            if (!qryFaPendenzgruppe.Post()) return;

            //uid = qryVerfuegbar["UserID"];

            qryZugeteilt.Insert();
            qryZugeteilt["UserID"] = qryVerfuegbar["UserID"];
            qryZugeteilt["FaPendenzgruppeID"] = qryFaPendenzgruppe["FaPendenzgruppeID"];
            qryZugeteilt.Post();

            DisplayMembers();
        }

        private void btnRemoveAll_Click(object sender, System.EventArgs e)
        {
            if (qryFaPendenzgruppe.Count == 0 || qryZugeteilt.Count == 0) return;
            if (qryFaPendenzgruppe.Row.RowState == DataRowState.Added) return;

            DBUtil.ExecSQL("DELETE FROM FaPendenzgruppe_User WHERE FaPendenzgruppeID = {0}", qryFaPendenzgruppe["FaPendenzgruppeID"]);

            DisplayMembers();
        }

        private void btnRemove_Click(object sender, System.EventArgs e)
        {
            if (qryFaPendenzgruppe.Count == 0 || qryZugeteilt.Count == 0) return;

            qryZugeteilt.Delete();
            DisplayMembers();
        }

