        #region Private Methods

        private void btnZuordnen_Click(object sender, System.EventArgs e)
        {
            if( DBUtil.IsEmpty(edtLeistung.EditValue) || DBUtil.IsEmpty(edtPersonNr.EditValue) )
            {
                KissMsg.ShowError("Bitte Person und Leistung auswählen!");
                return;
            }

            foreach(DataRow row in qryDocument.DataTable.Rows)
            {
                if( (bool)row["Sel"] == true )
                {
                    DBUtil.ExecSQLThrowingException( @"
                      INSERT INTO FaDokumente (FaLeistungID, Stichwort, DocumentID, BaPersonID, ErstelltUserID, ErstelltDatum, MutiertUserID, MutiertDatum)
                      VALUES ({0}, 'Manuell Nachmigriert', {1}, {2}, {3}, {4}, {3}, {4})", edtLeistung.EditValue, row["DocumentID"], edtPersonNr.LookupID, Session.User.UserID, DateTime.Now);
                }
            }
            qryDocument.Refresh();
        }

        private void edtPersonNr_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            string SearchString = edtPersonNr.Text.Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            KissLookupDialog dlg = new KissLookupDialog();
            if (e.ButtonClicked || !DBUtil.IsEmpty(SearchString))
            {
                if (DBUtil.IsEmpty(SearchString))
                    SearchString = "%";

                e.Cancel = !dlg.SearchData(@"
                    SELECT
                      ID$              = BaPersonID,
                      DisplayText$     = DisplayText,
                      [Name]           = DisplayText,
                      [AHV-Nummer]     = AHVNummer,
                      Wohnsitz
                    FROM vwPerson
                    WHERE DisplayText LIKE '%' + {0} + '%'
                    ORDER BY NameVorname
                    ", SearchString, e.ButtonClicked);

                if (!e.Cancel)
                {
                    edtPersonNr.EditValue = dlg["DisplayText$"];
                    edtPersonNr.LookupID = dlg["ID$"];

                    edtLeistung.Properties.DataSource = DBUtil.OpenSQL( @"
                      SELECT ID$ = FaLeistungID, Text$ = 'Fall ' + cast(FAL.FaFallID as varchar) + ', ' + dbo.fnLOVText('FaProzess',LEI.FaProzessCode)
                      FROM FaLeistung     LEI
                        INNER JOIN FaFall FAL ON FAL.FaFallID = LEI.FaFallID
                      WHERE LEI.FaProzessCode IN (200,201) AND (LEI.BaPersonID = {0} OR FAL.BaPersonID = {0})", dlg["ID$"]);
                }
            }
        }

