            ((System.ComponentModel.ISupportInitialize)(this.grvMigMessage)).EndInit();
            this.pnlTitel.ResumeLayout(false);
            ((System.ComponentModel.ISupportInitialize)(this.lblFehlerTitel)).EndInit();
            this.ResumeLayout(false);

        }

        #endregion

        #region Dispose

        /// <summary>
        /// Clean up any resources being used.
        /// </summary>
        protected override void Dispose(bool disposing)
        {
            if (disposing)
            {
                if ((components != null))
                {
                    components.Dispose();
                }
            }
            base.Dispose(disposing);
        }

        #endregion

        #region Public Methods

        //private void kissSearch_RunSearch(object sender, EventArgs e)
        public override void OnSearch()
        {
            RunSearch();
        }

        #endregion

        #region Private Methods

        private void RunSearch()
        {
            Cursor currentCursor = Cursor.Current;
            try
            {
                Cursor.Current = Cursors.WaitCursor;
                if( lastSelectedTab == tpgSuchen )
                {
                    if( !DBUtil.IsEmpty(qryTypes["DarstellungSQL"]) )
                    {
                        grvMigMessage.Columns.Clear();
                        grdMigMessage.DataSource = null;
                        qryMigMessage.DataTable.Columns.Clear();
                        qryMigMessage.Fill(qryTypes["DarstellungSQL"] as string);
                        grdMigMessage.DataSource = qryMigMessage;
                        foreach( DevExpress.XtraGrid.Columns.GridColumn col in grvMigMessage.Columns )
                            col.Width = col.GetBestWidth();
                    }
                    lblFehlerTitel.Text = qryTypes["Name"] as string;
                    tabControl.SelectTab(tpgListe);
                    lastSelectedTab = tpgListe;
                }
                else
                {
                    tabControl.SelectTab(tpgSuchen);
                    lastSelectedTab = tpgSuchen;
                }
            }
            finally
            {
                Cursor.Current = currentCursor;
            }
        }

        private void qryTypes_PositionChanged(object sender, System.EventArgs e)
        {
            tpgListe.HideTab = DBUtil.IsEmpty(qryTypes["DarstellungSQL"]);
        }

        private void tabControl_SelectedTabChanged(SharpLibrary.WinControls.TabPageEx page)
        {
            RunSearch();
        }

        #endregion
    }
