        private void qryQJVorschlag_BeforeDelete(object sender, System.EventArgs e)
        {
            DBUtil.ExecSQL(@"DELETE KaVermittlungEinsatz WHERE KaVermittlungVorschlagID = {0}", qryQJVorschlag["KaVermittlungVorschlagID"]);
        }

        private void qryQJVorschlag_BeforePost(object sender, System.EventArgs e)
        {
            if (!CheckDate(edtLehreVon, edtLehreBis))
            {
                KissMsg.ShowInfo("CtlKaQJExterneEinsaetze", "EinsatzVonVorBis2", MESSAGE_CHECK_DATUMVON_BIS);
                throw new KissCancelException();
            }
            if (!DBUtil.IsEmpty(edtSchnuppernVon.EditValue) && !DBUtil.IsEmpty(edtSchnuppernBis.EditValue))
                qryQJVorschlag["Schnuppern"] = string.Format("{0:d}", edtSchnuppernVon.EditValue) + " - " + string.Format("{0:d}", edtSchnuppernBis.EditValue);

            if (!DBUtil.IsEmpty(edtSchnuppernBis.EditValue) && !DBUtil.IsEmpty(edtSchnuppernVon.EditValue))
                qryQJVorschlag["Schnuppern"] = string.Format("{0:d}", edtSchnuppernVon.EditValue) + " - " + string.Format("{0:d}", edtSchnuppernBis.EditValue);
        }

        private void qryQJVorschlag_PositionChanged(object sender, System.EventArgs e)
        {
            if (qryQJVorschlag.Count == 0)
            {
                qryQJEinsatz.Fill(DBNull.Value);
            }
            else
            {
                qryQJEinsatz.Fill(qryQJVorschlag["KaVermittlungVorschlagID"]);
            }
        }

