        #region Public Methods

        public override object GetContextValue(string FieldName)
        {
            switch (FieldName.ToUpper())
            {
                case "FALEISTUNGID":
                    return faLeistungID;
                case "BAPERSONID":
                    return (int)DBUtil.ExecuteScalarSQL("select BaPersonID from FaLeistung where FaLeistungID = {0}", faLeistungID);
                case "KAVERMITTLUNGVORSCHLAGID":
                    return qryQJVorschlag["KaVermittlungVorschlagID"];
                case "OWNERUSERID":
                    return (int)DBUtil.ExecuteScalarSQL("select UserID from FaLeistung where FaLeistungID = {0}", faLeistungID);
            }

            return base.GetContextValue(FieldName);
        }

        public void Init(string maskName, Image maskImage, int FaLeistungID, int BaPersonID)
        {
            this.lblTitle.Text = maskName;
            this.imageTitle.Image = maskImage;
            this.faLeistungID = FaLeistungID;
            this.baPersonID = BaPersonID;

            tabQJExtern.SelectedTabIndex = 0;
            qryQJVorschlag.Fill(faLeistungID, Session.User.IsUserKA ? 0 : 1);

            DBUtil.GetFallRights(this.faLeistungID, out MayRead, out MayIns, out MayUpd, out MayDel, out MayClose, out MayReopen);
            qryQJEinsatz.CanUpdate = qryQJVorschlag.CanUpdate && DBUtil.UserHasRight("CtlKaQJExterneEinsaetze", "U") && MayUpd;
            qryQJEinsatz.CanInsert = qryQJVorschlag.CanInsert && DBUtil.UserHasRight("CtlKaQJExterneEinsaetze", "I") && MayIns;
            qryQJEinsatz.CanDelete = qryQJVorschlag.CanDelete && DBUtil.UserHasRight("CtlKaQJExterneEinsaetze", "D") && MayDel;
            qryQJEinsatz.EnableBoundFields();

            btnDetailsEinsatzplatz.Enabled = qryQJVorschlag.Count > 0 && (qryQJEinsatz.CanUpdate || qryQJEinsatz.CanInsert || qryQJEinsatz.CanDelete);
        }

        public override bool ReceiveMessage(System.Collections.Specialized.HybridDictionary param)
        {
            // we need at least one parameter to know what to do
            if (param == null || param.Count < 1)
            {
                // by default, nothing to do
                return true;
            }

            // action depending
            switch (param["Action"] as string)
            {
                case "SelectRow":
                    if (!param.Contains("KaVermittlungVorschlagID")) param["KaVermittlungVorschlagID"] = -1;

                    tabQJExtern.SelectedTabIndex = 0;
                    qryQJEinsatz.Find(string.Format("KaVermittlungVorschlagID = {0}", param["KaVermittlungVorschlagID"]));

                    return true;
            }

            // did not understand message
            return false;
        }

        #endregion

        #region Private Methods

        private bool CheckDate(KiSS4.Gui.KissDateEdit dateFrom, KiSS4.Gui.KissDateEdit dateTo)
        {
            bool rslt = true;

            if (DBUtil.IsEmpty(dateFrom.EditValue) || DBUtil.IsEmpty(dateTo.EditValue))
            {
                rslt = true;
            }
            else
            {
                if (Convert.ToDateTime(dateFrom.EditValue) > Convert.ToDateTime(dateTo.EditValue))
                    rslt = false;
            }

            return rslt;
        }

        private void datLeaveEinsatz(object sender, System.EventArgs e)
        {
            if (!CheckDate(edtLehreVon, edtLehreBis))
            {
                KissMsg.ShowInfo("CtlKaQJExterneEinsaetze", "EinsatzVonVorBis2", MESSAGE_CHECK_DATUMVON_BIS);
                edtLehreBis.EditValue = null;
                qryQJEinsatz["EinsatzBis"] = null;
                qryQJVorschlag["StageBis"] = null;
                edtLehreBis.Focus();
            }
        }

        private void datLeaveSchnuppern(object sender, System.EventArgs e)
        {
            if (!CheckDate(edtSchnuppernVon, edtSchnuppernBis))
            {
                KissMsg.ShowInfo("CtlKaQJExterneEinsaetze", "SchnuppernVonVorBis", "'bis' darf nicht vor 'Schnuppern von' liegen!");
                edtSchnuppernBis.EditValue = null;
                qryQJVorschlag["SchnuppernBis"] = null;
                edtSchnuppernBis.Focus();
            }
        }

