        private void qryBank_PositionChanged(object sender, EventArgs e)
        {
            bool canUpdate = !(bool)qryBank["SicUpdated"];
            EditModeType editMode;
            qryBank.CanUpdate = canUpdate;
            if (canUpdate)
            {
                editMode = EditModeType.Normal;
            }
            else
            {
                editMode = EditModeType.ReadOnly;
            }

            edtName.EditMode = editMode;
            edtClearingNr.EditMode = editMode;
            edtClearingNrNeu.EditMode = editMode;
            edtFilialeNr.EditMode = editMode;
            edtGueltigAb.EditMode = editMode;
            edtHauptsitzBCL.EditMode = editMode;
            edtLandCode.EditMode = editMode;
            edtOrt.EditMode = editMode;
            edtPCKontoNr.EditMode = editMode;
            edtPLZ.EditMode = editMode;
            edtStrasse.EditMode = editMode;
            edtZusatz.EditMode = editMode;
        }
    }
