        #region Public Methods

        public void Init(string TitleName, Image TitleImage, int baPersonID)
        {
            this.baPersonID = baPersonID;
              this.picTitel.Image = TitleImage;
              this.lblTitel.Text  = TitleName;

              SqlQuery qryLetzteFallID = DBUtil.OpenSQL(@"SELECT FaFallID, DatumBis FROM dbo.fnFaFall_GetActive({0})",
            baPersonID);
              letzteFallID = Utils.ConvertToInt(qryLetzteFallID["FaFallID"]);

              // falls letzter Fall bereits geschlossen, keine nderungen mehr zulassen
              if (!DBUtil.IsEmpty(qryLetzteFallID["DatumBis"]))
              {
            qryFaInvolviertePerson.CanInsert = false;
            qryFaInvolviertePerson.CanUpdate = false;
            qryFaInvolviertePerson.CanDelete = false;
              }

              // alle Personen aller Klientensysteme von BaPersonID
              SqlQuery qryBetrifft = DBUtil.OpenSQL(@"
            select 	distinct
                Code = PRS.BaPersonID,
                Text = PRS.Name + isnull(' '+PRS.Vorname,'')
            from dbo.FaFall FAL with(readuncommitted)
            inner join dbo.FaFallPerson FAP with(readuncommitted) on FAP.FaFallID = FAL.FaFallID
            inner join dbo.BaPerson     PRS with(readuncommitted) on PRS.BaPersonID = FAP.BaPersonID
            where	FAL.BaPersonID = {0}
            union all select null, ''
            order by Text",
            baPersonID);

              edtBetroffenePerson.Properties.DataSource = qryBetrifft;
              colBetrifft.ColumnEdit = grdInvolviertePerson.GetLOVLookUpEdit(qryBetrifft);

              //Involvierte Personen
              OpenQuery();

              // Wenn alles leer, dann eine neuen Zeile einfgen
              if (qryFaInvolviertePerson.Count == 0 && qryFaInvolviertePerson.CanInsert) qryFaInvolviertePerson.Insert();
        }

        #endregion

        #region Private Methods

        private void OpenQuery()
        {
            if (chkNurLetzterFall.Checked)
              {
            colFall.GroupIndex = -1;
            qryFaInvolviertePerson.Fill(baPersonID,letzteFallID);
              }
              else
              {
            colFall.GroupIndex = 0;
            qryFaInvolviertePerson.Fill(baPersonID,null);
            grvInvolviertePerson.ExpandAllGroups();
              }
        }

        private void chkNurLetzterFall_CheckedChanged(object sender, EventArgs e)
        {
            if (qryFaInvolviertePerson.RowModified)
            if (!qryFaInvolviertePerson.Post())
              throw new KissCancelException();
              OpenQuery();
        }

        private void qryFaInvolviertePerson_AfterFill(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 07.08.2007 : sozheo : alles neu
            // ------------------------------------------------------------------------------

            if (qryFaInvolviertePerson.Count==0)
              qryFaInvolviertePerson_PositionChanged(sender, e);
        }

        private void qryFaInvolviertePerson_AfterInsert(object sender, System.EventArgs e)
        {
            // Vorgabewerte einfgen
            qryFaInvolviertePerson["FaFallID"] = letzteFallID;
            qryFaInvolviertePerson["DatumVon"] = DateTime.Today;

            ctlErfassungMutation1.ShowInfo();

            edtRolle.Focus();
        }

        private void qryFaInvolviertePerson_BeforePost(object sender, System.EventArgs e)
        {
            DBUtil.CheckNotNullField(edtName, lblName.Text);

            //Rolle oder Rolle Zusatz mssen erfast sein
            if (DBUtil.IsEmpty(qryFaInvolviertePerson["RolleCode"]) && DBUtil.IsEmpty(qryFaInvolviertePerson["RolleBemerkung"]))
            {
                edtRolle.Focus();
                KissMsg.ShowInfo("Eines der beiden Felder 'Rolle' und 'Rolle Zusatz' mssen erfasst werden.");
                throw new KissCancelException();
            }

            // Kontrollieren, dass Datum von kleiner als Datum bis ist:
            if (!DBUtil.IsEmpty(qryFaInvolviertePerson["DatumVon"]) && !DBUtil.IsEmpty(qryFaInvolviertePerson["DatumBis"]))
            {
                if ((DateTime)qryFaInvolviertePerson["DatumVon"] >= (DateTime)qryFaInvolviertePerson["DatumBis"])
                    {
                        edtDatumVon.Focus();
                        KissMsg.ShowInfo("CtlBaInvolviertePerson", "DatumsFehler",
                            "Das Von-Datum muss kleiner sein als das Bis-Datum.");
                        throw new KissCancelException();
                    }
                }
            ctlErfassungMutation1.SetFields();
        }

        private void qryFaInvolviertePerson_PositionChanged(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 07.08.2007 : sozheo : alles neu
            // ------------------------------------------------------------------------------

            if ((qryFaInvolviertePerson.Count==0) || ((int)qryFaInvolviertePerson["FaFallID"] != letzteFallID))
              qryFaInvolviertePerson.EnableBoundFields(false);
            else
              qryFaInvolviertePerson.EnableBoundFields(true);

            ctlErfassungMutation1.ShowInfo();
        }

