        public void Init(string TitleName, Image TitleImage, int baPersonID)
        {
            this.baPersonID = baPersonID;
            this.picTitel.Image = TitleImage;

            SqlQuery qryLetzteFallID = DBUtil.OpenSQL(@"SELECT * FROM dbo.fnFaFall_GetActive({0})",
          baPersonID);
            letzteFallID = Utils.ConvertToInt(qryLetzteFallID["FaFallID"]);

            // falls letzter Fall bereits geschlossen, keine Änderungen mehr zulassen
            if (!DBUtil.IsEmpty(qryLetzteFallID["DatumBis"]))
            {
                qryFaInvolvierteInstitution.CanInsert = false;
                qryFaInvolvierteInstitution.CanUpdate = false;
                qryFaInvolvierteInstitution.CanDelete = false;
            }

            // alle Personen im Klientensystem von BaPersonID
            //"where F.FaFallID = " + letzteFallID.ToString() +
            // Es sollen die FallPersonen aller Fälle de aktuellen fallträgers angezeigt werden:
            // also nicht: "where F.FaFallID = " + letzteFallID.ToString() +
            SqlQuery qryBetrifft = DBUtil.OpenSQL(
          "select Code = P.BaPersonID, " +
          "Text = IsNull(P.Vorname+ ' ','') + P.Name, " +
          "InActualCase = case when F.FaFallID = " + letzteFallID.ToString() + " then 1 else 0 end " +
          "from FaFallPerson F " +
          "left join BaPerson P on P.BaPersonID = F.BaPersonID " +
          "where F.FaFallID in (" +
            "select Q.FaFallID from FaFall Q where Q.BaPersonID = " + baPersonID.ToString() +
          ") order by 2");
            edtBetrifftPerson.Properties.DataSource = qryBetrifft;
            colBetrifft.ColumnEdit = grdFaInvolvierteInstitution.GetLOVLookUpEdit(qryBetrifft);

            // Institutionen zu diesen Fall
            OpenData();

            // Wenn Query leer ist, dannn soll ein neuer Datensatz eingefügt werden:
            if (qryFaInvolvierteInstitution.Count == 0 && qryFaInvolvierteInstitution.CanInsert)
                qryFaInvolvierteInstitution.Insert();

            this.edtInfo.Properties.Appearance.Font = new System.Drawing.Font("Arial", 9.75F, System.Drawing.FontStyle.Regular, System.Drawing.GraphicsUnit.Point, ((byte)(0)));
        }

        #endregion

        #region Private Methods

        private void OpenData()
        {
            if (chkNurLetzterFall.Checked)
            {
                colFall.GroupIndex = -1;
                qryFaInvolvierteInstitution.Fill(baPersonID, letzteFallID);
            }
            else
            {
                colFall.GroupIndex = 0;
                qryFaInvolvierteInstitution.Fill(baPersonID, null);
                grvFaInvolvierteInstitution.ExpandAllGroups();
            }
        }

        private void chkNurLetzterFall_CheckedChanged(object sender, EventArgs e)
        {
            if (qryFaInvolvierteInstitution.RowModified)
                if (!qryFaInvolvierteInstitution.Post())
                    throw new KissCancelException();

            OpenData();
        }

