        private void edtAnsprechPerson2_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            // Letzte Bearbeitung
            // 07.08.2007 : sozheo : alles neu
            // 03.10.2007 : sozheo : Feld Standard existiert nicht, deshlab entfernt
            // ------------------------------------------------------------------------------

            if (DBUtil.IsEmpty(qryFaInvolvierteInstitution["BaInstitutionID"]))
            {
                KissMsg.ShowInfo("Wählen Sie bitte zuerst eine Institution!");
                return;
            }

            KissLookupDialog dlg = new KissLookupDialog();
            if (dlg.SearchData(@"
              SELECT Anrede, Person, Telefon, EMail
              FROM   BaInstitutionKontakt INK
              WHERE  BaInstitutionID = {0} AND
                     Aktiv = 1
              ORDER BY Person",
              qryFaInvolvierteInstitution["BaInstitutionID"].ToString(),
              e.ButtonClicked, null, null, null))
            {
                qryFaInvolvierteInstitution["Ansprechperson"] = dlg["Person"];
                qryFaInvolvierteInstitution["AnsprechpersonTelefon"] = dlg["Telefon"];
                qryFaInvolvierteInstitution["AnsprechpersonEmail"] = dlg["EMail"];
            }
        }

