        private void edtInstitutionName_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            // Letzte Bearbeitung
            // 07.08.2007 : sozheo : alles neu
            // 07.01.2008 : sozmim : nur definitive Institutionen auswählbar
            // ------------------------------------------------------------------------------

            string SearchString = edtInstitutionName.Text.Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            if (DBUtil.IsEmpty(SearchString))
            {
                if (e.ButtonClicked)
                {
                    SearchString = "%";
                }
                else
                {
                    qryFaInvolvierteInstitution["BaInstitutionID"] = DBNull.Value;
                    qryFaInvolvierteInstitution["Name"] = DBNull.Value;
                    qryFaInvolvierteInstitution["OrtStrasse"] = DBNull.Value;
                    qryFaInvolvierteInstitution["InstitutionTypCode"] = DBNull.Value;

                    qryInstitution.DataTable.Rows.Clear();
                    qryZahlInfos.DataTable.Rows.Clear();
                    return;
                }
            }

            KissLookupDialog dlg = new KissLookupDialog();
            e.Cancel = !dlg.SearchData(@"
              SELECT ID$                 = INS.BaInstitutionID,
                     Institution         = INS.Name,
                     Adresse             = INS.Adresse,
                     Typ                 = INS.Typ,
                     InstitutionTypCode$ = INS.InstitutionTypCode,
                     OrtStrasse$         = INS.OrtStrasse
              FROM   vwInstitution INS
              WHERE  INS.BaFreigabeStatusCode = 2 AND
                     (INS.Name LIKE '%' + {0} + '%' OR
                      INS.AdressZusatz LIKE '%' + {0} + '%')
              ORDER BY INS.Name",
              SearchString,
              e.ButtonClicked);

            if (!e.Cancel)
            {
                qryFaInvolvierteInstitution["BaInstitutionID"] = dlg[0];
                qryFaInvolvierteInstitution["Name"] = dlg[1];
                qryFaInvolvierteInstitution["InstitutionTypCode"] = dlg["InstitutionTypCode$"];
                qryFaInvolvierteInstitution["OrtStrasse"] = dlg["OrtStrasse$"];

                qryInstitution.Fill(dlg[0]);
                qryZahlInfos.Fill(dlg[0]);
            }
        }

