        private void qryFaInvolvierteInstitution_AfterInsert(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 31.07.2007 : sozheo : alles neu
            // ------------------------------------------------------------------------------

            qryFaInvolvierteInstitution["BaPersonID"] = baPersonID;
            qryFaInvolvierteInstitution["FaFallID"] = letzteFallID;
            qryFaInvolvierteInstitution["DatumVon"] = DateTime.Today;

            // angemeldeter User
            qryFaInvolvierteInstitution["UserID"] = Session.User.UserID;

            edtInstitutionName.Focus();
        }

        private void qryFaInvolvierteInstitution_BeforePost(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 07.08.2007 : sozheo : alles neu
            // ------------------------------------------------------------------------------

            DBUtil.CheckNotNullField(edtInstitutionName, lblInstitution.Text);

            // Kontrollieren, dass Datum von kleiner als Datum bis ist:
            if (!DBUtil.IsEmpty(qryFaInvolvierteInstitution["DatumVon"]) && !DBUtil.IsEmpty(qryFaInvolvierteInstitution["DatumBis"]))
            {
                if ((DateTime)qryFaInvolvierteInstitution["DatumVon"] >= (DateTime)qryFaInvolvierteInstitution["DatumBis"])
                {
                    edtDauerVon.Focus();
                    KissMsg.ShowInfo("CtlBaInvolvierteStelle", "DatumsFehler",
                      "Das Von-Datum muss kleiner sein als das Bis-Datum.",
                      520, 120);
                    throw new KissCancelException();
                }
            }
        }

