            // LookupSQL setzen:
            edtInstitutionLookup.LookupSQL = @"
              SELECT
                ID$           = INS.Name,
                Text$         = INS.Name,
                AdressZusatz$ = ADR.Zusatz,
                Postfach$     = ADR.Postfach,
                Strasse$      = ADR.Strasse,
                HausNr$       = ADR.HausNr,
                PLZ$          = ADR.PLZ,
                Ort$          = ADR.Ort,
                LandCode$     = ADR.BaLandID,
                Kanton$       = ADR.Kanton,
                Institution   = INS.Name,
                Adresse       = IsNull(ADR.Strasse + IsNull(' ' + ADR.HausNr, '') + ', ', '') +
                                IsNull(ADR.PLZ + ' ', '') + IsNull(ADR.Ort, ''),
                Typ           = TYP.Text
              FROM dbo.BaInstitution INS WITH(READUNCOMMITTED)
                   LEFT JOIN dbo.BaAdresse ADR WITH(READUNCOMMITTED) on ADR.BaInstitutionID = INS.BaInstitutionID
                   LEFT JOIN dbo.XLOVCode  TYP WITH(READUNCOMMITTED) on TYP.LOVName = 'BaInstitutionTyp' and
                                              TYP.Code = INS.InstitutionTypCode
              WHERE INS.BaFreigabeStatusCode = 2 AND (INS.Name like '%' + {0}+'%')
              ORDER BY INS.Name";

            // Anzeige im Gitter steuern:
            colAufenthaltAdresstyp.ColumnEdit = grdBaAufenthaltAdresse.GetLOVLookUpEdit((SqlQuery)cboAdresstyp.Properties.DataSource);

            // Rechte der Qry setzen
            SetRights();
        }

