        public bool CtlBaPersonAdresse_CanSaveData()
        {
            // kleiner Hack: falls der Inhalt des KissPLZOrt-Control verndert wurde, das Control
            // noch fokusiert ist und gespeichert wird, dann wird keine Validierung des Inhaltes durchgefhrt. Durch das
            // Verndern des Fokuses wird diese Validierung angestossen.
            if (edtPLZAufenthalt.UserModified)
            {
                cboAdresstypWohnsitz.Focus();
            }
            else if (edtPLZWohnsitz.UserModified)
            {
                cboAdresstyp.Focus();
            }
            if (!qryBaWohnsitzAdresse.Post()) return false;
            if (!qryBaAufenthaltAdresse.Post()) return false;

            // Damit die Daten unten auch gespeichert werden: !!!
            if (CtlBaPerson_SqlQuery != null) if (!CtlBaPerson_SqlQuery.Post()) return false;
            return true;
        }

