        private void CtlBaPersonAdresse_AddData(object sender, EventArgs e)
        {
            /*
              KissMsg.ShowInfo(
            "qryBaWohnsitzAdresse.CanInsert ) " + (qryBaWohnsitzAdresse.CanInsert ? "TRUE" : "FALSE") + "\r\n" +
            "qryBaWohnsitzAdresse.CanUpdate ) " + (qryBaWohnsitzAdresse.CanUpdate ? "TRUE" : "FALSE") + "\r\n" +
            "qryBaAufenthaltAdresse.CanInsert ) " + (qryBaAufenthaltAdresse.CanInsert ? "TRUE" : "FALSE") + "\r\n" +
            "qryBaAufenthaltAdresse.CanUpdate ) " + (qryBaAufenthaltAdresse.CanUpdate ? "TRUE" : "FALSE")
              );
              */

            // Kontrolle, ob eingefgt werden darf
            if (!qryBaAufenthaltAdresse.CanInsert && !qryBaWohnsitzAdresse.CanInsert)
            {
                KissMsg.ShowInfo("Sie besitzen keine Berechtigung zum Einfgen neuer Adressen.");
                return;
            }

            if (!qryBaWohnsitzAdresse.CanInsert)
            {
                // Wenn keine Berechtigung zum Einfgen von Wohnsitzen, dann automatisch einen neuen Aufenthalt einfgen
                if (!qryBaAufenthaltAdresse.Post()) return;
                qryBaAufenthaltAdresse.Insert();
            }
            else
                if (!qryBaAufenthaltAdresse.CanInsert)
                {
                    // Wenn keine Berechtigung zum Einfgen von Aufenthalten, dann automatisch einen neuen Wohnsitz einfgen
                    if (!qryBaWohnsitzAdresse.Post()) return;
                    qryBaWohnsitzAdresse.Insert();
                }
                else
                {
                    // Hier muss abgefragt werden, bei welcher Gitter eine neue Zeile eingefgt werden soll
                    string Msg = "Wollen Sie einen neuen Wohnsitz oder eine neue andere Adresse erstellen?";
                    string[] ButtonList = { "neuer &Wohnsitz", "andere &Adresse", "A&bbrechen" };
                    int ResultButtonIndex = KissMsg.ShowButtonDlg(Msg, ButtonList);

                    if (ResultButtonIndex == 0)
                    {
                        // Zuerst speichern, damit nachfolgende Kontrolle auch korrekt ist:
                        if (!qryBaWohnsitzAdresse.Post()) return;

                        // Kontrollieren, ob es bereits einen Datensatz gibt, bei welchem Datum von und DatumBis leer sind.
                        // Wenn dies der Fall ist, dann kein neuer Wohnsitz mehr eingefgt werden:
                        SqlQuery qryDat = DBUtil.OpenSQL(@"
                SELECT Strasse, PLZ, Ort
                FROM dbo.BaAdresse WHERE AdresseCode=1 and BaPersonID={0}
                and DatumVon IS NULL and DatumBis is NULL",
                          baPersonID);
                        if (qryDat.Count > 0)
                        {
                            string TmpMsg1 = "";
                            if (!DBUtil.IsEmpty(qryDat["Strasse"]))
                                TmpMsg1 += (string)qryDat["Strasse"] + "\r\n";
                            string TmpMsg2 = (string)qryDat["PLZ"] + " " + (string)qryDat["Ort"] + "\r\n\r\n";
                            string[] MsgList = { TmpMsg1, TmpMsg2 };
                            KissMsg.ShowInfo("CtlBaPersonAdresse", "CheckInsert",
                              "Es gibt ein Datensatz mit leeren Daten von und bis:\r\n\r\n" +
                              "{0}{1}" +
                              "Es kann deshalb kein neuer Wohnsitz hinzugefgt werden.\r\n\r\n" +
                              "ndern Sie zuerst das Datum von oder das Datum bis \r\ndes vorhandenen Datensatzes.",
                              520, 240, MsgList);
                            return;
                        }
                        // Einfgen in zivilrechtlicher Wohnsitz:
                        qryBaWohnsitzAdresse.Insert();
                    }
                    else if (ResultButtonIndex == 1)
                    {
                        // Einfgen in weitere Wohnsitz:
                        qryBaAufenthaltAdresse.Post();
                        qryBaAufenthaltAdresse.Insert();
                    }
                    else
                    {
                        // Es wurde abbrechen gedrckt, also hier nichts machen.
                        // Wenn notwendig, hier return aufrufen oder Exception erzeugen.
                    }
                }
        }

