        private void qryBaAufenthaltAdresse_BeforePost(object sender, System.EventArgs e)
        {
            edtPLZAufenthalt.DoValidate();

            // Letzte Bearbeitung
            // 31.07.2007 : sozheo : alles neu
            // 03.10.2007 : sozheo : Name einer Institution nur wenn Adresscode = Institutionstyp
            // ------------------------------------------------------------------------------------
            if (DBUtil.IsEmpty(qryBaAufenthaltAdresse["DatumVon"]))
            {
                KissMsg.ShowInfo("Der Wert 'Datum Von' muss ausgefllt werden.");
                edtAufenthaltVon.Focus();
                throw new KissCancelException();
            }
            // Kontrollieren, ob Adresstyp erfasst ist:
            if (DBUtil.IsEmpty(qryBaAufenthaltAdresse["AdresseCode"]))
            {
                cboAdresstyp.Focus();
                KissMsg.ShowInfo("CtlBaPersonAdresse", "AdressTypLeer",
                  "Der Adressetyp darf nicht leer sein.");
                throw new KissCancelException();
            }

            // Kontrollieren, dass Datum von kleiner als Datum bis ist:
            if (!DBUtil.IsEmpty(qryBaAufenthaltAdresse["DatumVon"]) && !DBUtil.IsEmpty(qryBaAufenthaltAdresse["DatumBis"]))
            {
                if ((DateTime)qryBaAufenthaltAdresse["DatumVon"] >= (DateTime)qryBaAufenthaltAdresse["DatumBis"])
                {
                    edtAufenthaltVon.Focus();
                    KissMsg.ShowInfo("CtlBaPersonAdresse", "DatumsFehler",
                      "Das Von-Datum muss kleiner sein als das Bis-Datum.");
                    throw new KissCancelException();
                }
            }

            // Kontrollieren, dass es keine berschneidungen im Datum gibt.
            int AktID = 0;
            if (!DBUtil.IsEmpty(qryBaAufenthaltAdresse["BaAdresseID"])) AktID = (int)qryBaAufenthaltAdresse["BaAdresseID"];

            string TmpSQL = "";
            TmpSQL += "SELECT DatumVon, DatumBis, Strasse, PLZ, Ort FROM dbo.BaAdresse";
            TmpSQL += " WHERE BaPersonID=" + BaPersonID.ToString();
            TmpSQL += " AND NOT BaAdresseID=" + AktID.ToString();
            TmpSQL += " AND AdresseCode=" + qryBaAufenthaltAdresse["AdresseCode"].ToString();

            if (DBUtil.IsEmpty(qryBaAufenthaltAdresse["DatumVon"]) && DBUtil.IsEmpty(qryBaAufenthaltAdresse["DatumBis"]))
            {
                // Wenn beide Daten des aktuellen Datensatzes leer sind,
                // dann darf kein anderer Datensatz existieren:
            }
            else if ((!DBUtil.IsEmpty(qryBaAufenthaltAdresse["DatumVon"])) && DBUtil.IsEmpty(qryBaAufenthaltAdresse["DatumBis"]))
            {
                // Wenn Datum von nicht leer ist, DatumBis aber leer ist,
                // dann darf es kein anderer Datensatz geben,
                // bei welchem ein Datum grsser als das DatumVon des aktuellen Datensatzes ist:
                TmpSQL += string.Format(
                  " and (DatumBis is NULL OR DatumVon >= '{0}' OR DatumBis >= '{0}') ",
                  ((DateTime)qryBaAufenthaltAdresse["DatumVon"]).ToString("yyyyMMdd"));
            }
            else if (DBUtil.IsEmpty(qryBaAufenthaltAdresse["DatumVon"]) && (!DBUtil.IsEmpty(qryBaAufenthaltAdresse["DatumBis"])))
            {
                // Wenn Datum bis nicht leer ist, DatumVon aber leer ist,
                // dann darf es kein anderer Datensatz geben,
                // bei welchem ein Datum kleiner als das DatumBis des aktuellen Datensatzes ist:
                TmpSQL += string.Format(
                  " and (DatumVon is NULL OR DatumVon <= '{0}' OR DatumBis <= '{0}') ",
                  ((DateTime)qryBaAufenthaltAdresse["DatumBis"]).ToString("yyyyMMdd"));
            }
            else
            {
                // Wenn DatumBis und DatumVon nicht leer ist,
                // dann darf es keine berschneidungen geben:
                TmpSQL += string.Format(
                  " and ( (DatumVon <= '{1}' AND DatumBis is NULL) " +
                     "or (DatumBis >= '{0}' AND DatumVon is NULL) " +
                     "or (DatumBis >= '{0}' AND DatumBis <= '{1}') " +
                     "or (DatumVon >= '{0}' AND DatumVon <= '{1}') ) ",
                  ((DateTime)qryBaAufenthaltAdresse["DatumVon"]).ToString("yyyyMMdd"),
                  ((DateTime)qryBaAufenthaltAdresse["DatumBis"]).ToString("yyyyMMdd"));
            }
            if ((int)qryBaAufenthaltAdresse["AdresseCode"] != 99)
            {
                SqlQuery qryDat = DBUtil.OpenSQL(TmpSQL);

                if (qryDat.Count > 0)
                {
                    string TmpMsg = "";
                    if (!DBUtil.IsEmpty(qryDat["Strasse"]))
                        TmpMsg += (string)qryDat["Strasse"] + "\r\n";
                    // v.a. bei aus Alpha importierten Adressen knnen PLZ und Ort leer sein
                    if (!DBUtil.IsEmpty(qryDat["PLZ"]) && !DBUtil.IsEmpty(qryDat["Ort"]))
                    {
                        TmpMsg += (string)qryDat["PLZ"] + " " + (string)qryDat["Ort"] + "\r\n";
                    }
                    // Strasse und Ort kann leer sein, aber zumindest datum von wird befllt sein
                    if (qryDat["DatumVon"] is DateTime)
                    {
                        TmpMsg += "von " + ((DateTime)qryDat["DatumVon"]).ToString("dd.MM.yyyy");
                        if (qryDat["DatumBis"] is DateTime)
                        {
                            TmpMsg += " bis " + ((DateTime)qryDat["DatumBis"]).ToString("dd.MM.yyyy");
                        }
                        TmpMsg += "\r\n";
                    }
                    TmpMsg += "\r\n";
                    string TmpTyp = cboAdresstyp.GetDisplayText(cboAdresstyp.EditValue);
                    //string[] MsgList = {TmpMsg, TmpTyp};
                    edtAufenthaltVon.Focus();
                    KissMsg.ShowInfo(string.Format(
                      "Es gibt mind. eine zeitliche berschneidung mit der Zeile:\r\n\r\n" +
                      "{0}" +
                      "ndern Sie das Datum von oder Datum bis des aktuellen Datensatzes\r\n" +
                      "oder ndern Sie zuerst Datum von oder Datum bis anderer Zeilen,\r\n" +
                      "welche den Adresstyp '{1}' haben.",
                      TmpMsg, TmpTyp));
                    throw new KissCancelException();
                }
            }

            // Kontrollieren, ob Name der Insitutuion erfasst ist ist:
            // nur wenn die Adresse eine Institution ist:
            if (Utils.ConvertToInt(qryBaAufenthaltAdresse["AdresseCode"]) == 13)
                if (DBUtil.IsEmpty(qryBaAufenthaltAdresse["InstitutionName"]))
                {
                    edtAufenthaltInstitutionName.Focus();
                    KissMsg.ShowInfo("CtlBaPersonAdresse", "InstitutionNameLeer",
                      "Der Institutionsname darf nicht leer sein.");
                    throw new KissCancelException();
                }

            if (DBUtil.IsEmpty(qryBaAufenthaltAdresse["PLZ"]))
            {
                edtPLZAufenthalt.Focus();
                KissMsg.ShowInfo("CtlBaPersonAdresse", "AdressPLZLeer",
                  "Die Postleitzahl der Adresse darf nicht leer sein.",
                  520, 120);
                throw new KissCancelException();
            }

            if (DBUtil.IsEmpty(qryBaAufenthaltAdresse["Ort"]))
            {
                edtPLZAufenthalt.Focus();
                KissMsg.ShowInfo("CtlBaPersonAdresse", "AdressOrtLeer",
                  "Die Ortschaft der Adresse darf nicht leer sein.",
                  520, 120);
                throw new KissCancelException();
            }
        }

