                if ((DateTime)qryBaWohnsitzAdresse["DatumVon"] >= (DateTime)qryBaWohnsitzAdresse["DatumBis"])
                {
                    edtWohnsitzVon.Focus();
                    KissMsg.ShowInfo("CtlBaPersonAdresse", "DatumsFehler",
                      "Das Von-Datum muss kleiner sein als das Bis-Datum.");
                    throw new KissCancelException();
                }
            }

            // Kontrollieren, dass es keine berschneidungen im Datum gibt.
            int AktID = 0;
            if (!DBUtil.IsEmpty(qryBaWohnsitzAdresse["BaAdresseID"])) AktID = (int)qryBaWohnsitzAdresse["BaAdresseID"];

            string TmpSQL = "";
            TmpSQL += "SELECT DatumVon, DatumBis, Strasse, PLZ, Ort FROM dbo.BaAdresse ";
            TmpSQL += " WHERE AdresseCode=1 AND BaPersonID=" + BaPersonID.ToString();
            TmpSQL += " AND NOT BaAdresseID=" + AktID.ToString();

            if (DBUtil.IsEmpty(qryBaWohnsitzAdresse["DatumVon"]) && DBUtil.IsEmpty(qryBaWohnsitzAdresse["DatumBis"]))
            {
                // Wenn beide Daten des aktuellen Datensatzes leer sind,
                // dann darf kein anderer Datensatz existieren:
            }
            else if ((!DBUtil.IsEmpty(qryBaWohnsitzAdresse["DatumVon"])) && DBUtil.IsEmpty(qryBaWohnsitzAdresse["DatumBis"]))
            {
                // Wenn Datum von nicht leer ist, DatumBis aber leer ist,
                // dann darf es kein anderer Datensatz geben,
                // bei welchem ein Datum grsser als das DatumVon des aktuellen Datensatzes ist:
                TmpSQL += string.Format(
                  " and (DatumBis is NULL OR DatumVon >= '{0}' OR DatumBis >= '{0}') ",
                  ((DateTime)qryBaWohnsitzAdresse["DatumVon"]).ToString("yyyyMMdd"));
            }
            else if (DBUtil.IsEmpty(qryBaWohnsitzAdresse["DatumVon"]) && (!DBUtil.IsEmpty(qryBaWohnsitzAdresse["DatumBis"])))
            {
                // Wenn Datum bis nicht leer ist, DatumVon aber leer ist,
                // dann darf es kein anderer Datensatz geben,
                // bei welchem ein Datum kleiner als das DatumBis des aktuellen Datensatzes ist:
                TmpSQL += string.Format(
                  " and (DatumVon is NULL OR DatumVon <= '{0}' OR DatumBis <= '{0}') ",
                  ((DateTime)qryBaWohnsitzAdresse["DatumVon"]).ToString("yyyyMMdd"));
            }
            else
            {
                // Wenn DatumBis und DatumVon nicht leer ist,
                // dann darf es keine berschneidungen geben:
                TmpSQL += string.Format(
                  " and ( (DatumVon <= '{1}' AND DatumBis is NULL) " +
                     "or (DatumBis >= '{0}' AND DatumVon is NULL) " +
                     "or (DatumBis >= '{0}' AND DatumBis <= '{1}') " +
                     "or (DatumVon >= '{0}' AND DatumVon <= '{1}') ) ",
                  ((DateTime)qryBaWohnsitzAdresse["DatumVon"]).ToString("yyyyMMdd"),
                  ((DateTime)qryBaWohnsitzAdresse["DatumBis"]).ToString("yyyyMMdd"));
            }

            SqlQuery qryDat = DBUtil.OpenSQL(TmpSQL);
            if (qryDat.Count > 0)
            {
                string TmpMsg = "";
                if (!DBUtil.IsEmpty(qryDat["Strasse"]))
                    TmpMsg += (string)qryDat["Strasse"] + "\r\n";
                TmpMsg += (string)qryDat["PLZ"] + " " + (string)qryDat["Ort"] + "\r\n\r\n";
                string[] MsgList = { TmpMsg };
                edtWohnsitzVon.Focus();
                KissMsg.ShowInfo(string.Format(
                  "Es gibt mind. eine zeitliche berschneidung, z.B. mit der Zeile:\r\n\r\n" +
                  "{0}" +
                  "ndern Sie das Datum von oder Datum bis des aktuellen Datensatzes\r\n" +
                  "oder ndern Sie zuerst das Von-Datum oder das Bis-Datum anderer Zeilen.",
                  MsgList));
                throw new KissCancelException();
            }

            if (DBUtil.IsEmpty(qryBaWohnsitzAdresse["PLZ"]))
            {
                // PLZ und Ort drfen nicht leer sein:
                edtPLZWohnsitz.Focus();
                KissMsg.ShowInfo("CtlBaPersonAdresse", "AdressPLZLeer",
                  "Die Postleitzahl der Adresse darf nicht leer sein.");
                throw new KissCancelException();
            }

            if (DBUtil.IsEmpty(qryBaWohnsitzAdresse["Ort"]))
            {
                // PLZ und Ort drfen nicht leer sein:
                edtPLZWohnsitz.Focus();
                KissMsg.ShowInfo("CtlBaPersonAdresse", "AdressOrtLeer",
                  "Die Ortschaft der Adresse darf nicht leer sein.");
                throw new KissCancelException();
            }
        }

        private void qryBaWohnsitzAdresse_PositionChanged(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 11.08.2008 : sozheo : Neu Filter fr BaLand
            // 14.08.2008 : sozheo : Editieren einstellen korrigiert
            // ---------------------------------------------------------------------------

            // Editieren einstellen
            // Es kann editiert werden, wenn Adresse nicht aus Alpha und Recht "CtlBaPersonAdresse_Bearbeiten" vorhanden
            // Es kann editiert werden, wenn Adresse aus Alpha und Recht "CtlBaPersonAdresse_AlphaAdressenBearbeiten" vorhanden
            bool adresseAusEinwohnerregister = Utils.ConvertToBool(qryBaWohnsitzAdresse["AusEinwohnerregister"], true);
            bool canEdit = (
              qryBaWohnsitzAdresse.CanUpdate &&
              qryBaWohnsitzAdresse.Count > 0 &&
              (!adresseAusEinwohnerregister || DBUtil.UserHasRight("CtlBaPersonAdresse_AlphaAdressenBearbeiten"))
            );
            qryBaWohnsitzAdresse.EnableBoundFields(canEdit);

            // Das Feld DatumBis soll immer editiert werden knnen
            if (
              !canEdit &&
              qryBaWohnsitzAdresse.CanUpdate &&
              qryBaWohnsitzAdresse.Count > 0 &&
              (!adresseAusEinwohnerregister || DBUtil.UserHasRight("CtlBaPersonAdresse_AlphaAdressenBearbeiten"))
            ) edtWohnsitzBis.EditMode = EditModeType.Normal;

            // Infos, ob Adresse fr Anzeige in Reports gesperrt ist oder nicht
            object adresseGesperrt = qryBaWohnsitzAdresse["Gesperrt"];
            lblAdresseGesperrt.Visible = (adresseGesperrt is bool && (bool)adresseGesperrt);

            // 11.08.2008 : sozheo : Neu Filter fr BaLand
            edtPLZWohnsitz.EdtLand.LOVFilterWhereAppend = true;
            //edtPLZWohnsitz.EdtLand.LOVFilter = string.Format("(Value2 = '1' or Code = {0})", Utils.ConvertToInt(qryBaWohnsitzAdresse["BaLandID"]));
            edtPLZWohnsitz.EdtLand.LOVFilter = string.Format("((DatumVon <= GETDATE() AND (DatumBis IS NULL OR DatumBis >= GETDATE())) or BaLandID = {0})", Utils.ConvertToInt(qryBaWohnsitzAdresse["BaLandID"]));
            edtPLZWohnsitz.EdtLand.LOVName = "BaLand";
        }

