        #region Public Methods

        public void Init(string TitleName, Image TitleImage, int baPersonID)
        {
            this.BaPersonID = baPersonID;
            this.picTitel.Image = TitleImage;
            this.lblTitel.Text = TitleName;

            SqlQuery qryLetzteFallID = DBUtil.OpenSQL(@"
            select	top 1 FaFallID, DatumBis
            from	FaFall
            where	BaPersonID = {0}
            order by DatumVon desc",
          baPersonID);
            letzteFallID = (int)qryLetzteFallID["FaFallID"];

            OpenAllQueries();
        }

        #endregion

        #region Private Methods

        private void CtlBaUebersicht_AddData(object sender, EventArgs e)
        {
            // Hier darf nicht eingefügt werden:
            KissMsg.ShowInfo("CtlBaUebersicht", "RelationenEinfuegen",
          "Hier können Sie keine Relationen einfügen.\r\n" +
          "Verwenden Sie für das Einfügen von Relationen das lokale Menu der Basis\r\n" +
          "(rechte Maustaste).");
            throw new KissCancelException();
        }

