        private void CtlBaUebersicht_DeleteData(object sender, EventArgs e)
        {
            // Hier darf nicht gelöscht werden:
            KissMsg.ShowInfo("CtlBaUebersicht", "RelationenLoeschen",
          "Hier können Sie keine Relationen löschen.\r\n" +
          "Verwenden Sie für das Löschen von Relationen das lokale Menu der Basis\r\n" +
          "(rechte Maustaste).");
            throw new KissCancelException();
        }

        private void CtlBaUebersicht_RefreshData(object sender, EventArgs e)
        {
            OpenAllQueries();
        }

        private void OpenAllQueries()
        {
            // Falltraeger
            qryFalltraeger.Fill(BaPersonID, letzteFallID);
            // Relationen
            qryRelation.Fill(letzteFallID, BaPersonID);
            // Inst.
            qryInvolvierteInstitution.Fill(letzteFallID);
            // Personen
            qryInvolviertePerson.Fill(letzteFallID);
        }

