        private void OpenSearch()
        {
            string tmpSelect = @"
            select
              P.BaPersonID,
              P.NNummer,
              P.Name,
              P.Vorname,
              P.Name + IsNull(' '+P.Vorname,'') as NameVorname,
              P.ZipNr,
              Strasse = case when IsNull(A.Gesperrt, 0) = 1 then '! ' end + A.Strasse,
              PLZ = case when IsNull(A.Gesperrt, 0) = 1 then '! ' end + A.PLZ,
              Ort = case when IsNull(A.Gesperrt, 0) = 1 then '! ' end + A.Ort,
              Wohnsitz = case when IsNull(A.Gesperrt, 0) = 1 then 'GESPERRT ' end + A.PLZ + IsNull(' '+A.Ort,'') + IsNull(', '+A.Strasse + IsNull(' '+A.HausNr,''),''),
              AufenthaltsOrt = (
            select top 1 Q.PLZ + IsNull(' '+Q.Ort,'') + IsNull(', '+Q.Strasse + IsNull(' '+Q.HausNr,''),'')
            from BaAdresse Q
            where Q.BaPersonID = P.BaPersonID
            and Q.AdresseCode = 3  -- Aufenthalt
            and GetDate() between IsNull(Q.DatumVon,GetDate()) and IsNull(Q.DatumBis,GetDate())
            order by Q.DatumVon desc
              ),
              Geschlecht = dbo.fnLOVText('BaGeschlecht', P.GeschlechtCode),
              GeschlechtKurz = dbo.fnLOVColumnListe('BaGeschlecht', P.GeschlechtCode, 'shortText'),
              Age = dbo.fnGetAge(P.Geburtsdatum,getDate()),
              P.Geburtsdatum,
              Geburtanzeige = Convert(varchar(10), P.Geburtsdatum, 104),
              Heimatort = G.Name,
              Nationalitaet = dbo.fnLOVText('BaNationalitaet', P.NationalitaetCode),
              AufenthaltsStatus = dbo.fnLOVText('BaAufenthaltStatus', P.AuslaenderStatusCode) + IsNull(', gültig bis '+Convert(varchar(10),P.AuslaenderStatusGueltigBis, 104), ''),
              Zivilstand = dbo.fnLOVText('BaZivilstand', P.ZivilstandCode) + IsNull(', seit '+Convert(varchar(10),P.Zivilstanddatum, 104), ''),
              FallNr = dbo.fnGetFallNrOfPerson(P.BaPersonID),
              IsCase = case when Exists(select F.FaFallID from FaFall F where F.BaPersonID = P.BaPersonID) then 1 else 0 end,
              LT = case when Exists(select L.FaLeistungID from FaLeistung L where L.BaPersonID = P.BaPersonID) then 1 else 0 end
            from BaPerson P
            left outer join BaAdresse A
              on A.BaPersonID=P.BaPersonID
              and A.BaAdresseID = (
            Select Top 1 C.BaAdresseID from BaAdresse C
            where C.BaPersonID=P.BaPersonID
            and C.AdresseCode = 1  -- Wohnsitz
            and GetDate() between IsNull(A.DatumVon,GetDate()) and IsNull(A.DatumBis,GetDate())
            order by C.DatumVon desc
              )
            left outer join BaGemeinde G on G.BaGemeindeID = P.HeimatGemeindeCode
            where 1=1";

            // Name
            if (!DBUtil.IsEmpty(edtNameX.EditValue))
                tmpSelect += " AND P.Name like " + DBUtil.SqlLiteralLike(edtNameX.Text + "%");

            // Vorname
            if (!DBUtil.IsEmpty(edtVornameX.EditValue))
                tmpSelect += " AND P.Vorname like " + DBUtil.SqlLiteralLike(edtVornameX.Text + "%");

            // Strasse
            if (!DBUtil.IsEmpty(edtStrasseX.EditValue))
                tmpSelect += " AND A.Strasse like " + DBUtil.SqlLiteralLike(edtStrasseX.Text + "%");

            // PLZ
            if (!DBUtil.IsEmpty(edtPLZX.EditValue))
                tmpSelect += " AND A.PLZ like " + DBUtil.SqlLiteralLike(edtPLZX.Text + "%");

            // Ort
            if (!DBUtil.IsEmpty(edtOrtX.EditValue))
                tmpSelect += "  AND A.Ort like " + DBUtil.SqlLiteralLike(edtOrtX.Text + "%");

            // Geburtsdatum
            if ((!DBUtil.IsEmpty(edtGeburtVonX.EditValue)) && (!DBUtil.IsEmpty(edtGeburtBisX.EditValue)))
                tmpSelect += string.Format(
                  " AND P.Geburtsdatum between '{0}' and '{1}' ",
                  (((DateTime)edtGeburtVonX.EditValue).ToString("yyyyMMdd")),
                  (((DateTime)edtGeburtBisX.EditValue).ToString("yyyyMMdd"))
                );
            else
                if ((DBUtil.IsEmpty(edtGeburtVonX.EditValue)) && (!DBUtil.IsEmpty(edtGeburtBisX.EditValue)))
                    tmpSelect += string.Format(
                      " AND P.Geburtsdatum <= '{0}' ",
                      (((DateTime)edtGeburtBisX.EditValue).ToString("yyyyMMdd"))
                    );
                else
                    if ((!DBUtil.IsEmpty(edtGeburtVonX.EditValue)) && (DBUtil.IsEmpty(edtGeburtBisX.EditValue)))
                        tmpSelect += string.Format(
                          " AND P.Geburtsdatum >= '{0}' ",
                          (((DateTime)edtGeburtVonX.EditValue).ToString("yyyyMMdd"))
                        );

            // Fallträger
            if (chkNurFaelleX.Checked)
                tmpSelect += " AND P.BaPersonID IN (Select distinct Q.BaPersonID from FaFall Q) ";

            // ID
            if (!DBUtil.IsEmpty(edtPersonIDX.EditValue))
                tmpSelect += " AND P.BaPersonID = " + DBUtil.SqlLiteralLike(edtPersonIDX.EditValue);

            // Zip
            if (!DBUtil.IsEmpty(edtZipNrX.EditValue))
                tmpSelect += " AND P.ZipNr = " + DBUtil.SqlLiteralLike(edtZipNrX.EditValue);

            // AHV
            if (!DBUtil.IsEmpty(edtAHVX.EditValue))
                tmpSelect += " AND P.AHVNummer like '%" + edtAHVX.EditValue + "%'";

            // Versicherten-Nummer
            if (!DBUtil.IsEmpty(edtVersichertenNrX.EditValue))
                tmpSelect += " AND P.NNummer = " + DBUtil.SqlLiteralLike(edtVersichertenNrX.EditValue);

            // Nationalitaet
            if (!DBUtil.IsEmpty(cboNationalitaetX.EditValue))
                tmpSelect += " AND P.NationalitaetCode = " + cboNationalitaetX.EditValue.ToString();

            // Sortierung:
            tmpSelect += " ORDER BY P.Name, P.Vorname";

            // SQL öffnen:
            qryPersonenStamm.Fill(tmpSelect);

            // TODO:
            // Leo 9.8.2007: Temporary code. User can now edit in "Personenstamm".
            // 		 Has to be changed!!!
            // ctlBaPerson.SetRights(qryPersonenStamm.Count > 0);
        }

