        private void tabUebersicht_SelectedTabChanging(object sender, System.ComponentModel.CancelEventArgs e)
        {
            e.Cancel = false;
            // Bevor das Register gewechselt wird ...
            if (tabUebersicht.SelectedTabIndex == 0)
            {
                // ... noch 1. Seite : Daten öffnen für anzeige:
                int tmpID = 0;
                if (!DBUtil.IsEmpty(qryPersonenStamm["BaPersonID"]))
                    tmpID = (int)qryPersonenStamm["BaPersonID"];
                ctlBaPerson.OpenData(tmpID);
                return;
            }
            else
            // ... noch 2. Seite : Daten speichern wenn nötig:
            {
                // Nicht wechseln, wenn Posten nicht ok:
                bool HasCorrections = ctlBaPerson.HasModifiedData();
                if (HasCorrections) e.Cancel = (!ctlBaPerson.PostData());
                if ((!e.Cancel) && HasCorrections) OpenSearch();
            }
        }

        private void tbpSuche_KeyUp(object sender, KeyEventArgs e)
        {
            if (e.KeyData == Keys.F3)
            {
                e.Handled = true;
                if (tabSearch.SelectedTabIndex == 0)
                {
                    // Suchfelder löschen:
                    edtNameX.EditValue = null;
                    edtVornameX.EditValue = null;
                    edtStrasseX.EditValue = null;
                    edtPLZX.EditValue = null;
                    edtOrtX.EditValue = null;
                    edtGeburtVonX.EditValue = null;
                    edtGeburtBisX.EditValue = null;
                    chkNurFaelleX.Checked = false;
                    edtPersonIDX.EditValue = null;
                    edtZipNrX.EditValue = null;
                    edtAHVX.EditValue = null;
                    edtVersichertenNrX.EditValue = null;
                    cboNationalitaetX.EditValue = null;
                    tabSearch.SelectedTabIndex = 1;
                }
                else if (tabSearch.SelectedTabIndex == 1)
                {
                    // Gitter neu anzeigen:
                    OpenSearch();
                    tabSearch.SelectedTabIndex = 0;
                }
            }
        }

