        public override bool ReceiveMessage(System.Collections.Specialized.HybridDictionary param)
        {
            // we need at least one parameter to know what to do
            if (param == null || param.Count < 1)
            {
                // by default, nothing to do
                return true;
            }

            // action depending
            switch (param["Action"] as string)
            {
                case "ShowPerson":
                    if (!qryBaSicherheitsleistung.Post()) return false;

                    NewSearch();

                    /*
                              foreach (Control C in Utils.AllControls(tpgSuchen))
                              {
                                    if (C is DevExpress.XtraEditors.BaseEdit)
                                         ((DevExpress.XtraEditors.BaseEdit)C).EditValue = null;
                              }
                              edtSucheMigriert.Checked = true;
                              edtSucheLaufend.Checked = true;
                              edtSucheNeu.Checked = true;
                    */
                    edtSucheBaPersonID.EditValue = param["BaPersonID"];
                    RunSearch();
                    tabControlSearch.SelectTab(tpgListe);
                    return true;
            }

            // did not understand message
            return base.ReceiveMessage(param);
        }

