        private void AddProLeistBuchung(object MigDetailBuchungID, string LAList)
        {
            if (qryBaSicherheitsleistung.Count == 0)
            {
                KissMsg.ShowInfo("Es ist keine Silei selektiert.");
                return;
            }

            if (DBUtil.IsEmpty(MigDetailBuchungID))
            {
                KissMsg.ShowInfo("Das Feld 'MigDetailBuchungID' muss ausgefllt sein.");
                return;
            }

            SqlQuery qry = DBUtil.OpenSQL(@"
              select BUC.KissLeistungsart, PRS.BaPersonID, PRS.NameVorname
              from   MigDetailBuchung BUC
                     left join BaSicherheitsleistungPosition SIP on SIP.MigDetailBuchungID = BUC.MigDetailBuchungID
                     left join BaSicherheitsleistung         SIL on SIL.BaSicherheitsleistungID = SIP.BaSicherheitsleistungID AND SIL.Geloescht = 0
                     left join vwPerson                      PRS on PRS.BaPersonID = SIL.BaPersonID
              where BUC.MigDetailBuchungID = {0}",
              MigDetailBuchungID);

            if (qry.Count == 0)
            {
                KissMsg.ShowInfo(string.Format("Eine ProLeist-Buchung mit MigDetailBuchungID = {0:0} existiert nicht", MigDetailBuchungID));
                return;
            }

            if (!DBUtil.IsEmpty(qry["BaPersonID"]))
            {
                KissMsg.ShowInfo(string.Format(
                     "Die ProLeist-Buchung mit MigDetailBuchungID = {0:0} ist bereits der Silei '{1:0} {2}' zugeordnet",
                     MigDetailBuchungID,
                     qry["BaPersonID"],
                     qry["NameVorname"]));
                return;
            }

            string LA = qry["KissLeistungsart"].ToString();
            if (LAList.IndexOf(LA) == -1)
            {
                KissMsg.ShowInfo(string.Format(
                  "Die ProLeist-Buchung mit MigDetailBuchungID = {0:0} hat die LA {1}.\r\n Zulssig sind: {2}",
                  MigDetailBuchungID,
                  LA,
                  LAList));
                return;
            }

            try
            {
                DBUtil.ExecSQLThrowingException(@"
                    insert BaSicherheitsleistungPosition (BaSicherheitsleistungID,MigDetailbuchungID)
                    values ({0},{1})",
                     qryBaSicherheitsleistung["BaSicherheitsleistungID"],
                     MigDetailBuchungID);

                qryKontostand.Refresh();
                qryKontostand.Find("MigDetailBuchungID = " + MigDetailBuchungID.ToString());
                qrySpesen.Refresh();
                qrySpesen.Find("MigDetailBuchungID = " + MigDetailBuchungID.ToString());
                UpdateSaldo();
            }
            catch (Exception ex)
            {
