        private void BelegLoeschen(SqlQuery qry)
        {
            if (DBUtil.IsEmpty(qry["KbBuchungBruttoID"])) return;
            int KbBuchungBruttoID = (int)qry["KbBuchungBruttoID"];

            if (!DBUtil.IsEmpty(qry["TransferDatum"]))
            {
                KissMsg.ShowError(CLASSNAME, "Beleg ist bereits bertragen", "Dieser Beleg ist bereits an PSCD bertragen und kann deshalb nicht gelscht werden");
                return;
            }

            if (KissMsg.ShowQuestion(string.Format("Sollen alle Positionen des Belegs ({0}) gelscht werden?", KbBuchungBruttoID)))
            {
                this.Cursor = Cursors.Default;
                try
                {
                    DBUtil.ExecSQLThrowingException(@"
                        delete BaSicherheitsleistungPosition
                        where  BaSicherheitsleistungID = {0} and
                               KbBuchungBruttoID = {1}

                        delete KbBuchungBrutto
                        where  KbBuchungBruttoID = {1}",
                         qryBaSicherheitsleistung["BaSicherheitsleistungID"],
                         KbBuchungBruttoID);

                    qryKontostand.Refresh();
                    qrySpesen.Refresh();
                    UpdateSaldo();
                }
                catch (Exception ex)
                {
                    KissMsg.ShowError(CLASSNAME, "DB-Fehler", "Fehler beim Lschen des Belegs", ex);
                }
                finally
                {
                    this.Cursor = Cursors.Default;
                }
            }
        }

