        private void btnRueckzahlungErzeugen_Click(object sender, System.EventArgs e)
        {
            qryBgKostenart.Fill();

            Session.BeginTransaction();
            try
            {
                if (edtBuchungsdatum.EditValue == null)
                    throw new KissErrorException("Das Feld 'Buchungsdatum' darf nicht leer bleiben");
                if (edtBuchungstext.EditValue == null)
                    throw new KissErrorException("Das Feld 'Buchungstext' darf nicht leer bleiben");

                object kbPeriodeID = DBUtil.ExecuteScalarSQLThrowingException("SELECT KbPeriodeID FROM KbPeriode WHERE PeriodeStatusCode = 1 AND {0} BETWEEN PeriodeVon AND PeriodeBis", edtBuchungsdatum.EditValue);
                if (kbPeriodeID == null || kbPeriodeID == DBNull.Value)
                    throw new KissErrorException(KissMsg.GetMLMessage(CLASSNAME, "KeineOffeneBuchungsperiode", "Fr das Datum {0:dd.MM.yyyy} besteht keine offene Buchungsperiode. Bitte geben Sie ein gltiges Buchungsdatum ein", KissMsgCode.MsgError, edtBuchungsdatum.EditValue));

                object debitorKtoNr = DBUtil.ExecuteScalarSQLThrowingException("SELECT * FROM KbKonto WHERE KbPeriodeID = {0} AND ',' + KbKontoartCodes + ',' LIKE '%,20,%'", kbPeriodeID);
                if (debitorKtoNr == null || debitorKtoNr == DBNull.Value)
                    throw new KissErrorException(KissMsg.GetMLMessage(CLASSNAME, "KeinDebitorKonto", "Fr das Datum {0:dd.MM.yyyy} ist kein Debitorkonto konfiguriert. Wenden Sie sich bitte an die SDS Finanzen", KissMsgCode.MsgError, edtBuchungsdatum.EditValue));

                object kostenstelle = DBUtil.ExecuteScalarSQLThrowingException(@"
                   SELECT ORG.Kostenstelle
                   FROM   FaLeistung          LEI
                     INNER JOIN XOrgUnit_User O2U ON O2U.UserID = LEI.UserID AND OrgUnitMemberCode = 2 /*Mitglied*/
                     INNER JOIN XOrgUnit      ORG ON ORG.OrgUnitID = O2U.OrgUnitID
                   WHERE FaLeistungID = {0}", qryBaSicherheitsleistung["FaLeistungID"]);
                if (kostenstelle == null || kostenstelle == DBNull.Value)
                    throw new KissErrorException(KissMsg.GetMLMessage(CLASSNAME, "KeineKostenstelle", "Fr das QT dieses Klienten ist keine Kostenstelle definiert. Bitte wenden Sie sich an die KiSS-Administration", KissMsgCode.MsgError));

                int id = (int)DBUtil.ExecuteScalarSQLThrowingException(@"INSERT INTO KbBuchungBrutto(KbPeriodeID, KbBuchungTypCode, BelegDatum, ValutaDatum, Betrag, Text, KbBuchungStatusCode, UserID, ModulID, Kostenstelle, Belegart, GruppierungUmbuchung, PscdKennzeichen)
                                                                         VALUES ( {0}, 2, {1}, {1}, $0.00, {2}, 2, {3}, 3, {4}, 'E7', 'SILEI', 'S' )
                                                                         SELECT CAST(SCOPE_IDENTITY() AS int)", kbPeriodeID, edtBuchungsdatum.EditValue, edtBuchungstext.EditValue, Session.User.UserID, kostenstelle);
                int positionImBeleg = 1;
                int baPersonID = (int)qryBaSicherheitsleistung["ST"];
                int baPersonIDStadt = 499998;

                bool dummyPerson = (baPersonID == 168563 || baPersonID == 168564);
                string buchungstextPrefix = dummyPerson ? edtBuchungstext.EditValue.ToString() : null;

                if (edtRESicherheitsleistung.EditValue != null && (decimal)edtRESicherheitsleistung.EditValue != 0m)
                {
                    int? slArt = qryBaSicherheitsleistung["BaMieteSicherheitsleistungArtCode"] as int?;
                    if (!slArt.HasValue && slArt.Value != 2 && slArt.Value != 3)
                        throw new KissErrorException(KissMsg.GetMLMessage(CLASSNAME, "KeinMDAS", "Diese Sicherheitsleistung ist weder Mietzinsdepot noch Anteilsschein und kann nicht bebucht werden", KissMsgCode.MsgError));
                    string rueckzahlungLA = edtOhneVereinbarung.Checked ? "862" : slArt.Value == 2 /*MD*/ ? "861" : "860" /*AS*/;
                    InsertDetailPosition(id, baPersonID, positionImBeleg++, rueckzahlungLA, (decimal)edtRESicherheitsleistung.EditValue, false, edtBuchungstext.EditValue.ToString(), edtBuchungsdatum.EditValue, null);
                }
                if (edtMieterschaeden.EditValue != null && (decimal)edtMieterschaeden.EditValue != 0m)
                    InsertDetailPosition(id, baPersonID, positionImBeleg++, "311", -(decimal)edtMieterschaeden.EditValue, false, edtBuchungstext.EditValue.ToString(), edtBuchungsdatum.EditValue, null);
                // BruttoZins
                if (edtZinsBruttoAelter.EditValue != null && (decimal)edtZinsBruttoAelter.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "041", (decimal)edtZinsBruttoAelter.EditValue, true, lblAeltereJahre.Text, edtBuchungsdatum.EditValue, buchungstextPrefix);
                if (edtZinsBruttoVorVor.EditValue != null && (decimal)edtZinsBruttoVorVor.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "041", (decimal)edtZinsBruttoVorVor.EditValue, true, lblVorVorJahr.Text, edtBuchungsdatum.EditValue, buchungstextPrefix);
                if (edtZinsBruttoVor.EditValue != null && (decimal)edtZinsBruttoVor.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "041", (decimal)edtZinsBruttoVor.EditValue, true, lblVorJahr.Text, edtBuchungsdatum.EditValue, buchungstextPrefix);
                if (edtZinsBruttoLaufend.EditValue != null && (decimal)edtZinsBruttoLaufend.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "041", (decimal)edtZinsBruttoLaufend.EditValue, true, lblLaufendesJahr.Text, edtBuchungsdatum.EditValue, buchungstextPrefix);
                // Spesen
                if (edtSpesenAelter.EditValue != null && (decimal)edtSpesenAelter.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "031", -(decimal)edtSpesenAelter.EditValue, true, lblAeltereJahre.Text, edtBuchungsdatum.EditValue, buchungstextPrefix);
                if (edtSpesenVorVor.EditValue != null && (decimal)edtSpesenVorVor.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "031", -(decimal)edtSpesenVorVor.EditValue, true, lblVorVorJahr.Text, edtBuchungsdatum.EditValue, buchungstextPrefix);
                if (edtSpesenVor.EditValue != null && (decimal)edtSpesenVor.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "031", -(decimal)edtSpesenVor.EditValue, true, lblVorJahr.Text, edtBuchungsdatum.EditValue, buchungstextPrefix);
                if (edtSpesenLaufend.EditValue != null && (decimal)edtSpesenLaufend.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "031", -(decimal)edtSpesenLaufend.EditValue, true, lblLaufendesJahr.Text, edtBuchungsdatum.EditValue, buchungstextPrefix);
                // Verrechnungssteuer
                if (edtVerrSteuerAelter.EditValue != null && (decimal)edtVerrSteuerAelter.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "033", -(decimal)edtVerrSteuerAelter.EditValue, true, null, edtBuchungsdatum.EditValue, buchungstextPrefix);
                if (edtVerrSteuerVorVor.EditValue != null && (decimal)edtVerrSteuerVorVor.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "034", -(decimal)edtVerrSteuerVorVor.EditValue, true, lblVorVorJahr.Text, edtBuchungsdatum.EditValue, buchungstextPrefix);
                if (edtVerrSteuerVor.EditValue != null && (decimal)edtVerrSteuerVor.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "034", -(decimal)edtVerrSteuerVor.EditValue, true, lblVorJahr.Text, edtBuchungsdatum.EditValue, buchungstextPrefix);
                if (edtVerrSteuerLaufend.EditValue != null && (decimal)edtVerrSteuerLaufend.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "034", -(decimal)edtVerrSteuerLaufend.EditValue, true, lblLaufendesJahr.Text, edtBuchungsdatum.EditValue, buchungstextPrefix);
                // Solidarittsbeitrag
                if (edtSolidBeitragAelter.EditValue != null && (decimal)edtSolidBeitragAelter.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "032", -(decimal)edtSolidBeitragAelter.EditValue, true, lblAeltereJahre.Text, edtBuchungsdatum.EditValue, buchungstextPrefix);
                if (edtSolidBeitragVorVor.EditValue != null && (decimal)edtSolidBeitragVorVor.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "032", -(decimal)edtSolidBeitragVorVor.EditValue, true, lblVorVorJahr.Text, edtBuchungsdatum.EditValue, buchungstextPrefix);
                if (edtSolidBeitragVor.EditValue != null && (decimal)edtSolidBeitragVor.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "032", -(decimal)edtSolidBeitragVor.EditValue, true, lblVorJahr.Text, edtBuchungsdatum.EditValue, buchungstextPrefix);
                if (edtSolidBeitragLaufend.EditValue != null && (decimal)edtSolidBeitragLaufend.EditValue != 0m)
                    InsertDetailPosition(id, baPersonIDStadt, positionImBeleg++, "032", -(decimal)edtSolidBeitragLaufend.EditValue, true, lblLaufendesJahr.Text, edtBuchungsdatum.EditValue, buchungstextPrefix);
                decimal summe = (decimal)DBUtil.ExecuteScalarSQLThrowingException(@"
                  UPDATE KBB
                  SET Betrag = Summe
                  FROM KbBuchungBrutto  KBB
                    INNER JOIN (SELECT Summe = SUM(Betrag), KbBuchungBruttoID FROM KbBuchungBruttoPerson GROUP BY KbBuchungBruttoID )KBP ON KBP.KbBuchungBruttoID = KBB.KbBuchungBruttoID
                  WHERE KBB.KbBuchungBruttoID = {0}

                  SELECT Betrag FROM KbBuchungBrutto WHERE KbBuchungBruttoID = {0}", id);
                if (summe < 0m)
                    throw new KissErrorException(KissMsg.GetMLMessage(CLASSNAME, "KeineForderung", "Aus den eingegebenen Zahlen resultiert eine Auszahlung und keine Forderung.", KissMsgCode.MsgError));
                // Buchung mit Sicherheitsleistung verknpfen
                DBUtil.ExecSQLThrowingException(@"
                  INSERT INTO BaSicherheitsleistungPosition (BaSicherheitsleistungID, BaSicherheitsleistungPositionCode, KbBuchungBruttoID)
                  VALUES ({0}, 2, {1})", qryBaSicherheitsleistung["BaSicherheitsleistungID"], id);

                if (DBUtil.IsEmpty(edtRESicherheitsleistung.EditValue) && DBUtil.IsEmpty(edtMieterschaeden.EditValue))
                {
                    //nur Zinsbuchungen
                    tabInstitution.SelectedTabIndex = 2;
                }
                else
                {
                    tabInstitution.SelectedTabIndex = 1;
                }
                ClearBuchungEdits();
                UpdateSaldo();

                // Refresh
                qryKontostand.Refresh();
                qryKontostand.Find(string.Format("KbBuchungBruttoID = {0}", id));
                qrySpesen.Refresh();
                qrySpesen.Find(string.Format("KbBuchungBruttoID = {0}", id));

                Session.Commit();
            }
            catch (KissCancelException ex)
            {
                if (Session.Transaction != null)
                    Session.Rollback();
                ex.ShowMessage();
            }
            catch (Exception ex)
            {
                if (Session.Transaction != null)
                    Session.Rollback();
                KissMsg.ShowError(CLASSNAME, "BuchungKannNichtErzeugtWerden", "Buchung kann nicht erzeugt werden", ex);
            }
        }

