        private void edtInstitution_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            string SearchString = edtInstitution.Text.Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            if (DBUtil.IsEmpty(SearchString))
            {
                if (e.ButtonClicked)
                {
                    SearchString = "%";
                }
                else
                {
                    qryBaSicherheitsleistung["BaInstitutionID"] = DBNull.Value;
                    qryBaSicherheitsleistung["BaBankID"] = DBNull.Value;
                    qryBaSicherheitsleistung["Institution"] = DBNull.Value;
                    qryBaSicherheitsleistung["ZusatzInfo"] = DBNull.Value;
                    return;
                }
            }

            KissLookupDialog dlg = new KissLookupDialog();

            // Zeige Banken und Institutionen
            e.Cancel = !dlg.SearchData(@"
                        SELECT
                        Institution         = BNK.Name,
                        Adresse             = BNK.Strasse + ', ' + BNK.PLZ + ' ' + BNK.Ort,
                        [Typ / Clearing-Nr.]= BNK.ClearingNr,
                        Institution$        = BNK.Name,
                        ZusatzInfo$         = isNull(BNK.Strasse + char(13) + char(10),'') + BNK.PLZ + ' ' + BNK.Ort,
                        BaInstitutionID$    = null,
                        BaBankID$           = BNK.BaBankID
                FROM   BaBank BNK
                WHERE  BNK.Name + BNK.Ort LIKE '%' + {0} + '%'
                AND BNK.ClearingNrNeu IS NULL
            UNION
                SELECT Institution         = INS.Name,
                        Adresse             = INS.Adresse,
                        [Typ / Clearing-Nr.]= INS.Typ,
                        Institution$        = INS.Name,
                        ZusatzInfo$         = INS.AdresseMehrzeilig,
                        BaInstitutionID$    = INS.BaInstitutionID,
                        BaBankID$           = null
                FROM   vwInstitution INS
                WHERE  INS.BaFreigabeStatusCode = 2 AND
                        (INS.Name LIKE '%' + {0} + '%' OR
                        INS.AdressZusatz LIKE '%' + {0} + '%')
                ORDER BY Institution",
                SearchString,
                e.ButtonClicked);

            if (!e.Cancel)
            {
                qryBaSicherheitsleistung["Institution"] = dlg["Institution$"];
                qryBaSicherheitsleistung["ZusatzInfo"] = dlg["ZusatzInfo$"];
                qryBaSicherheitsleistung["BaInstitutionID"] = dlg["BaInstitutionID$"];
                qryBaSicherheitsleistung["BaBankID"] = dlg["BaBankID$"];
            }
        }

