        private void SendToPscd(SqlQuery qry)
        {
            try
            {
                int? id = qry["KbBuchungBruttoID"] as int?;
                if (!id.HasValue)
                    return;
                this.Cursor = Cursors.WaitCursor;
                if (KiSS4.FAMOZ.PSCDServices.PSCDInterface.SubmitWhBuchungsstoff(null, new int[] { id.Value }))
                {
                    qryKontostand.Refresh();
                    qrySpesen.Refresh();
                    KissMsg.ShowInfo("Beleg wurde erfolgreich bertragen");
                }
                else
                {
                    // sollte nicht vorkommen, bei Fehler gibts Exception
                    KissMsg.ShowInfo("Beleg konnte nicht bermittelt werden");
                }
            }
            catch (Exception ex)
            {
                KissMsg.ShowError(CLASSNAME, "PscdFehler", "Fehler beim bertragen des Belegs", ex);
            }
            finally
            {
                this.Cursor = Cursors.Default;
            }
        }

        private void StornoBruttoBuchung(int kbBuchungBruttoID)
        {
            try
            {
                Cursor = Cursors.WaitCursor;
                PSCDInterface.StornoBruttoBelege(new int[] { kbBuchungBruttoID }, false, DateTime.Today);
                KissMsg.ShowInfo(CLASSNAME, "Beleg storniert", "Beleg wurde erfolgreich storniert");
            }
            catch (KissCancelException ex)
            {
                ex.ShowMessage();
            }
            catch (Exception ex)
            {
                KissMsg.ShowError(ex.Message);
            }
            finally
            {
                Cursor = Cursors.Default;
                qryKontostand.Refresh();
                qrySpesen.Refresh();
            }
        }

        private void Umbuchen(SqlQuery qry)
        {
            if (!qryBaSicherheitsleistung.Post()) return;

            List<string> selectRows = new List<string>();
            foreach (DataRow siLei in qryBaSicherheitsleistung.DataTable.Rows)
            {
                if ((int)qryBaSicherheitsleistung["BaSicherheitsleistungID"] == (int)siLei["BaSicherheitsleistungID"])
                    continue;

                selectRows.Add(string.Format("SELECT BaSicherheitsleistungID$ = {0}, Art = {1}, Klient = {2}, Auszahlung = {3}, Rckzahlung = {4}, Strasse = {5}, Ort = {6}, Institution = {7}, [Konto-Nr.] = {8}, Saldo = CAST({9} as varchar)",
                                             siLei["BaSicherheitsleistungID"],
                                             DBUtil.SqlLiteral(siLei["Art"]),
                                             DBUtil.SqlLiteral(siLei["KlientLine"]),
                                             DBUtil.SqlLiteral(siLei["AuszahlungAm"]),
                                             DBUtil.SqlLiteral(siLei["RueckzahlungAm"]),
                                             DBUtil.SqlLiteral(siLei["ObjektStrasse"]),
                                             DBUtil.SqlLiteral(siLei["ObjektOrt"]),
                                             DBUtil.SqlLiteral(siLei["Institution"]),
                                             DBUtil.SqlLiteral(siLei["KontoNummer"]),
                                             DBUtil.SqlLiteral(siLei["Saldo"])));
            }

            if (selectRows.Count == 0)
            {
                return;
            }

            string sql = string.Join(" UNION ALL ", selectRows.ToArray());

            KissLookupDialog dlg = new KissLookupDialog();
            bool cancel = !dlg.SearchData(sql, null, true);

            /*
            bool cancel = !dlg.SearchData(@"
                 select BaSicherheitsleistungID$ = SIC.BaSicherheitsleistungID,
                          Art         = dbo.fnLOVText('BaMieteSicherheitsleistungArt',SIC.BaMieteSicherheitsleistungArtCode),
                          Auszahlung  = SIC.AuszahlungAm,
                          Rckzahlung = SIC.RueckzahlungAm,
                          Strasse     = SIC.ObjektStrasse + isnull(' ' + SIC.ObjektHausNr,''),
                          Ort         = SIC.ObjektPLZ + ' ' + SIC.ObjektOrt,
                          Institution = isnull(INS.Name, BNK.Name),
                          [Konto-Nr.] = SIC.KontoNummer
                 from   BaSicherheitsleistung SIC
                          inner join vwPerson      PRS on PRS.BaPersonID = SIC.BaPersonID
                          left  join vwInstitution INS on INS.BaInstitutionID = SIC.BaInstitutionID
                          left  join BaBank        BNK on BNK.BaBankID = SIC.BaBankID
                 where SIC.BaPersonID = " + qryBaSicherheitsleistung["BaPersonID"].ToString() + @"
                 order by case when SIC.BaSicherheitsleistungID = " + qryBaSicherheitsleistung["BaSicherheitsleistungID"].ToString() + @" then 0 else 1 end,
                         Auszahlung",   //aktuell zugeordnete Sicherheitsleistung zuoberst
                 "",
                 true,null,null,null);
            */
            if (cancel) return;

            qry["BaSicherheitsleistungID"] = dlg["BaSicherheitsleistungID$"];
            qry.Post();
            if (qry == qryKontostand)
            {
                DateTime? auszahlung = dlg["Auszahlung"] as DateTime?;
                DateTime? valutaUmbuchung = qry["ValutaDatum"] as DateTime?;
                if (valutaUmbuchung.HasValue && (!auszahlung.HasValue || auszahlung.Value > valutaUmbuchung.Value))
                {
                    DBUtil.ExecSQLThrowingException("UPDATE BaSicherheitsleistung SET AuszahlungAm = {0} WHERE BaSicherheitsleistungID = {1}", valutaUmbuchung.Value, dlg["BaSicherheitsleistungID$"]);
                }
                qryBaSicherheitsleistung.Post();
            }
            qryBaSicherheitsleistung.Refresh();
            //qryBaSicherheitsleistung_PositionChanged(null, null);
        }

