        private void UpdateSaldo()
        {
            //
            SqlQuery qry = DBUtil.OpenSQL(@"
            SELECT SaldoPer = CASE WHEN (MAX(KBB.ValutaDatum)   IS NULL OR MAX(KBU.ValutaDatum) >= MAX(KBB.ValutaDatum)) AND
                                    (MAX(MIG.Buchungsdatum) IS NULL OR MAX(KBU.ValutaDatum) >= MAX(MIG.Buchungsdatum)) THEN MAX(KBU.ValutaDatum)
                               WHEN (MAX(MIG.Buchungsdatum) IS NULL OR MAX(KBB.ValutaDatum) >= MAX(MIG.Buchungsdatum)) THEN MAX(KBB.ValutaDatum)
                               ELSE                                                                                         MAX(MIG.Buchungsdatum)
                          END,
               Saldo = isNull(SUM(MIG.Betrag), 0) + isNull(SUM(KBK.Betrag), 0) - isNull(SUM(KBP.Betrag), 0)

            FROM BaSicherheitsleistung                      SIC
              INNER JOIN      dbo.BaSicherheitsleistungPosition SIP  ON SIP.BaSicherheitsleistungID = SIC.BaSicherheitsleistungID
            --mig
              LEFT OUTER JOIN dbo.MigDetailBuchung              MIG  ON MIG.MigDetailBuchungID = SIP.MigDetailBuchungID
            --netto (ausgaben)
              LEFT OUTER JOIN dbo.KbBuchung                     KBU  ON KBU.KbBuchungID        = SIP.KbBuchungID AND
                                                                        KBU.KbBuchungStatusCode NOT IN (9) -- Rcklufer ausschliessen
              LEFT OUTER JOIN dbo.KbBuchungKostenart            KBK  ON KBK.KbBuchungID        = KBU.KbBuchungID
              LEFT OUTER JOIN dbo.BgKostenart                   BKAN ON BKAN.BgKostenartID     = KBK.BgKostenartID
            --brutto (einnahmen)
              LEFT OUTER JOIN dbo.KbBuchungBrutto               KBB  ON KBB.KbBuchungBruttoID  = SIP.KbBuchungBruttoID AND KBB.Betrag >= 0 AND SIP.KbBuchungID IS NULL
              LEFT OUTER JOIN dbo.KbBuchungBruttoPerson         KBP  ON KBP.KbBuchungBruttoID  = KBB.KbBuchungBruttoID
              LEFT OUTER JOIN dbo.BgKostenart                   BKAB ON BKAB.BgKostenartID     = KBP.SpezBgKostenartID
            WHERE SIC.Geloescht = 0 AND
                  SIP.BaSicherheitsleistungID = {0} AND
                 (MIG.KissLeistungsart in ('320','321','860','861','862')
                  OR BKAN.KontoNr      in ('320','321','860','861','862')
                  OR BKAB.KontoNr      in ('320','321','860','861','862'))",
            qryBaSicherheitsleistung["BaSicherheitsleistungID"]);

            qryBaSicherheitsleistung["Saldo"] = qry["Saldo"];
            qryBaSicherheitsleistung["SaldoPer"] = qry["SaldoPer"];
            qryBaSicherheitsleistung.Row.AcceptChanges();
            qryBaSicherheitsleistung.RowModified = true;
        }

