        private void btnDefinitiv_Click(object sender, System.EventArgs e)
        {
            if (!qryBaInstitution.Post()) return;

            Session.BeginTransaction();
            try
            {
                ctlZahlungsweg.SetAllZahlungsverbindungenDefinitiv();

                qryBaInstitution["BaFreigabeStatusCode"] = 2; //definitiv
                qryBaInstitution["DefinitivUserID"] = Session.User.UserID;
                qryBaInstitution["DefinitivDatum"] = DateTime.Now;
                qryBaInstitution["Definitiv"] = Session.User.LogonName + ", " + DateTime.Now.ToString("dd.MM.yyyy");

                if (qryBaInstitution.Post())
                    btnDefinitiv.Visible = false;

                Session.Commit();
            }
            catch (KissCancelException ex)
            {
                Session.Rollback();
                KissMsg.ShowInfo(typeof(FrmInstitutionenStamm).Name, "DefinitivsetzenNichtErlaubtKeinDokument", "Die Institution kann nicht definitiv gesetzt werden, da nicht bei allen Zahlwegen ein zugehriges Dokument erfasst wurde.");

                //reload previous state in CtlBaZahlungsweg
                ctlZahlungsweg.BaInstitutionID = (int) qryBaInstitution["BaInstitutionID"];
            }
            catch (Exception ex)
            {
                Session.Rollback();
                KissMsg.ShowError(ex.Message);
            }
        }

        private void btnSendToPscd_Click(object sender, System.EventArgs e)
        {
            if (DBUtil.IsEmpty(qryBaInstitution["BaInstitutionID"]))
                return;

            try
            {
                KiSS4.FAMOZ.PSCDServices.PSCDInterface.CreateAndSubmitBusinessPartnerInstitutionData((int)qryBaInstitution["BaInstitutionID"]);
                KissMsg.ShowInfo("Institution erfolgreich an PSCD gesendet");
            }
            catch (KissCancelException ex)
            {
                ex.ShowMessage();
            }
            catch (Exception ex)
            {
                KissMsg.ShowError(ex.Message);
            }
        }

