        private void OpenInstitutionen()
        {
            //Cursor.Current = Cursors.WaitCursor;
            qryBaInstitution.SelectStatement = qryBaInstitution_SelectStatement + @"
            WHERE 1 = 1
            ";

            // ID
            if (!DBUtil.IsEmpty(editBaInstitutionIDX.EditValue))
                qryBaInstitution.SelectStatement +=
                "\r\n  AND INS.BaInstitutionID = " + editBaInstitutionIDX.EditValue.ToString();

            // Name
            if (editNameX.Text != "") qryBaInstitution.SelectStatement +=
          "\r\n  AND (INS.Name LIKE " + DBUtil.SqlLiteralLike("%" + editNameX.Text + "%") +
          "\r\n    OR ADR.Zusatz LIKE " + DBUtil.SqlLiteralLike("%" + editNameX.Text + "%") + ")";

            // Zusatz
            if (edtZusatzX.Text != "")
                qryBaInstitution.SelectStatement +=
                "\r\n  AND ADR.Zusatz like " + DBUtil.SqlLiteralLike("%" + edtZusatzX.Text + "%");

            // Strasse
            if (edtStrasseX.Text != "")
                qryBaInstitution.SelectStatement +=
                "\r\n  AND ADR.Strasse like " + DBUtil.SqlLiteralLike("%" + edtStrasseX.Text + "%");

            // HausNr
            if (edtHausNrX.Text != "")
                qryBaInstitution.SelectStatement +=
                "\r\n  AND ADR.HausNr like " + DBUtil.SqlLiteralLike(edtHausNrX.Text + "%");

            // PLZ
            if (editPLZX.Text != "")
                qryBaInstitution.SelectStatement +=
                "\r\n  AND ADR.PLZ like " + DBUtil.SqlLiteralLike(editPLZX.Text + "%");

            // Ort
            if (editOrtX.Text != "")
                qryBaInstitution.SelectStatement +=
                "\r\n  AND ADR.Ort like " + DBUtil.SqlLiteralLike(editOrtX.Text + "%");

            // EMail
            if (editEMailX.Text != "")
                qryBaInstitution.SelectStatement +=
                "\r\n  AND INS.EMail like " + DBUtil.SqlLiteralLike(editEMailX.Text + "%");

            // Telefon
            if (editTelefonX.Text != "")
                qryBaInstitution.SelectStatement +=
                "\r\n  AND INS.Telefon like " + DBUtil.SqlLiteralLike(editTelefonX.Text + "%");

            // Typ
            if (!DBUtil.IsEmpty(editInstitutionTypX.EditValue))
                qryBaInstitution.SelectStatement +=
                "\r\n  AND INS.InstitutionTypCode = " + DBUtil.SqlLiteral(editInstitutionTypX.EditValue);

            // Status
            if (!DBUtil.IsEmpty(edtFreigabeStatusCodeX.EditValue))
                qryBaInstitution.SelectStatement +=
                "\r\n  AND INS.BaFreigabeStatusCode = " + DBUtil.SqlLiteral(edtFreigabeStatusCodeX.EditValue);

            // Definitiv seit
            if (!DBUtil.IsEmpty(edtDefinitivSeitX.EditValue))
                qryBaInstitution.SelectStatement +=
                "\r\n  AND INS.DefinitivDatum >= " + DBUtil.SqlLiteral(edtDefinitivSeitX.EditValue);

            // nur Debitoren
            if (edtNurDebitorenX.Checked)
                qryBaInstitution.SelectStatement +=
                "\r\n  AND INS.Debitor = 1";

            // nur Kreditoren
            if (edtNurKreditorenX.Checked)
                qryBaInstitution.SelectStatement +=
                "\r\n  AND INS.Kreditor = 1";

            // Suche nach Kontaktangaben:
            if ((!DBUtil.IsEmpty(edtKontaktPersonX.EditValue)) ||
            (!DBUtil.IsEmpty(edtKontaktTelefonX.EditValue)) ||
            (!DBUtil.IsEmpty(edtKontaktEmailX.EditValue)))
            {
                qryBaInstitution.SelectStatement += @"
              AND EXISTS (SELECT BaInstitutionKontaktID
                      FROM dbo.BaInstitutionKontakt
                      WHERE BaInstitutionID = INS.BaInstitutionID";

                if (!DBUtil.IsEmpty(edtKontaktPersonX.EditValue))
                    qryBaInstitution.SelectStatement +=
                    "\r\n  AND Person like " + DBUtil.SqlLiteral("%" + edtKontaktPersonX.EditValue + "%");
                if (!DBUtil.IsEmpty(edtKontaktTelefonX.EditValue))
                    qryBaInstitution.SelectStatement +=
                    "\r\n  AND Telefon like " + DBUtil.SqlLiteral("%" + edtKontaktTelefonX.EditValue + "%");
                if (!DBUtil.IsEmpty(edtKontaktEmailX.EditValue))
                    qryBaInstitution.SelectStatement +=
                    "\r\n  AND Email like " + DBUtil.SqlLiteral("%" + edtKontaktEmailX.EditValue + "%");

                qryBaInstitution.SelectStatement += "\r\n  )";
            }

            if ((!DBUtil.IsEmpty(edtBankKontoNummer.EditValue)) ||
            (!DBUtil.IsEmpty(edtPostKontoNummer.EditValue)) ||
            (!DBUtil.IsEmpty(edtClearingNummer.EditValue)) ||
            (!DBUtil.IsEmpty(edtPCBank.EditValue)) ||
            (!DBUtil.IsEmpty(edtIBANNummer.EditValue)))
            {
                qryBaInstitution.SelectStatement += @"
              AND EXISTS (SELECT BaZahlungswegID
                      FROM dbo.BaZahlungsweg   BAZ
                      LEFT JOIN dbo.BaBank BAN ON BAN.BaBankID = BAZ.BaBankID
                      WHERE BAZ.BaInstitutionID = INS.BaInstitutionID";

                // Bankkontonummer
                if (!DBUtil.IsEmpty(edtBankKontoNummer.EditValue))
                    qryBaInstitution.SelectStatement +=
                      "\r\n  AND BAZ.BankKontoNummer like " + DBUtil.SqlLiteral("%" + edtBankKontoNummer.EditValue + "%");

                // Postkontonummer
                if (!DBUtil.IsEmpty(edtPostKontoNummer.EditValue))
                    qryBaInstitution.SelectStatement +=
                      "\r\n  AND BAZ.PostKontoNummer like " + DBUtil.SqlLiteral("%" + edtPostKontoNummer.Text.Replace("-", "%") + "%");

                // BCN
                if (!DBUtil.IsEmpty(edtClearingNummer.EditValue))
                    qryBaInstitution.SelectStatement +=
                      "\r\n  AND BAN.ClearingNr like " + DBUtil.SqlLiteral("%" + edtClearingNummer.EditValue + "%");

                //PC Bank
                if (!DBUtil.IsEmpty(edtPCBank.EditValue))
                    qryBaInstitution.SelectStatement +=
                      "\r\n  AND BAN.PCKontoNr like " + DBUtil.SqlLiteral("%" + edtPCBank.Text.Replace("-", "%") + "%");

                // IBAN
                if (!DBUtil.IsEmpty(edtIBANNummer.EditValue))
                    qryBaInstitution.SelectStatement +=
                      "\r\n  AND BAZ.IBANNummer like " + DBUtil.SqlLiteral("%" + edtIBANNummer.EditValue + "%");

                qryBaInstitution.SelectStatement += "\r\n)";
            }

            qryBaInstitution.SelectStatement += "\r\n ORDER BY INS.Name, PLZOrt";

            //KissMsg.ShowInfo(qryBaInstitution.SelectStatement);

            qryBaInstitution.Fill();
            lblRowCount.Text = qryBaInstitution.Count.ToString();

            //Cursor.Current = Cursors.Default;
        }

