        {
            // Letzte Bearbeitung
            // 30.07.2007 : sozheo : neu erstellt
            // ---------------------------------------------------------------------

            SetFieldProperties_Institution();

            int tmpID = 0;
            if (!DBUtil.IsEmpty(qryBaInstitution["BaInstitutionID"]))
                tmpID = (int)qryBaInstitution["BaInstitutionID"];

            qryBaAdresse.Fill(tmpID);
            SetFieldProperties_Adresse("");

            if (tmpID == 0 && qryBaInstitution.Count > 0 && qryBaAdresse.CanInsert)
            {
                // Bei neuer Zeile:
                qryBaAdresse.Insert();
            }

            qryBaInstitutionKontakt.CanInsert = (qryBaInstitution.CanUpdate && CanEditData());
            qryBaInstitutionKontakt.CanDelete = (qryBaInstitution.CanUpdate && CanEditData());
            qryBaInstitutionKontakt.CanUpdate = (qryBaInstitution.CanUpdate && CanEditData());
            qryBaInstitutionKontakt.Fill(qryBaInstitution["BaInstitutionID"]);

            bool CanEdit = (CanEditData());
            ctlZahlungsweg.SetRights(
              (qryBaInstitution.CanInsert && CanEdit),
              (qryBaInstitution.CanDelete && CanEdit),
              (qryBaInstitution.CanUpdate && CanEdit)
            );
            ctlZahlungsweg.BaInstitutionID = tmpID;

            ctlErfassungMutation1.ShowInfo();

            object freigabe = qryBaInstitution["BaFreigabeStatusCode"];
            btnDefinitiv.Visible = (freigabe is int && (int)freigabe == 1 && HasRightDefinitivSetzen && qryBaInstitution.Count > 0);
        }

