        public void SetFields()
        {
            try
            {
                if (ActiveSQLQuery.Row.RowState == DataRowState.Added)
                {
                    ActiveSQLQuery[DataMemberErfassungUserID] = Session.User.UserID;
                    ActiveSQLQuery[DataMemberErfassungDatum] = DateTime.Now;
                    ActiveSQLQuery[DataMemberMutationUserID] = Session.User.UserID;
                    ActiveSQLQuery[DataMemberMutationDatum] = DateTime.Now;
                }

                // XDocument performs forced Posts, were mutation information should not be changed
                // we take care of this by introducing "IsSilentPostingFromXDocument" here:
                else if (!ActiveSQLQuery.IsSilentPostingFromXDocument)
                {
                    ActiveSQLQuery[DataMemberMutationUserID] = Session.User.UserID;
                    ActiveSQLQuery[DataMemberMutationDatum] = DateTime.Now;
                }

                this.ShowInfo();
            }
            catch { }
        }

        public void ShowInfo()
        {
            try
            {
                SqlQuery qry = DBUtil.OpenSQL(@"
                            select Erfassung  = isNull((select LogonName + ', ' + convert(varchar,{1},104) from XUser where UserID = {0}),'---'),
                                   Mutation   = isNull((select LogonName + ', ' + convert(varchar,{3},104) from XUser where UserID = {2}),'---'),
                                   ToolTipErf = isNull((select LogonNameVornameOrgUnit from vwUser where UserID = {0}),'') + isNull((select ', ' + convert(varchar,{1},104) from XUser where UserID = {0}),''),
                                   ToolTipMut = isNull((select LogonNameVornameOrgUnit from vwUser where UserID = {2}),'') + isNull((select ', ' + convert(varchar,{3},104) from XUser where UserID = {2}),'')",
                    ActiveSQLQuery[DataMemberErfassungUserID],
                    ActiveSQLQuery[DataMemberErfassungDatum],
                    ActiveSQLQuery[DataMemberMutationUserID],
                    ActiveSQLQuery[DataMemberMutationDatum]);

                if (qry.Count > 0)
                {
                    lblUserErfassung.Text = qry["Erfassung"].ToString();
                    lblUserMutation.Text = qry["Mutation"].ToString();
                    toolTip1.SetToolTip(lblUserErfassung, qry["ToolTipErf"].ToString());
                    toolTip1.SetToolTip(lblUserMutation, qry["ToolTipMut"].ToString());
                }
            }
            catch
            {
                lblUserErfassung.Text = "---";
                lblUserMutation.Text = "---";
                toolTip1.SetToolTip(lblUserErfassung, String.Empty);
                toolTip1.SetToolTip(lblUserMutation, String.Empty);
            }
        }

