        #region Private Methods

        private void RefreshDependentRowsTable()
        {
            qryDependentRows.Fill("exec [spGetDependendRowCount] 'FaFall', {0}", edtFall.LookupID);
        }

        private void btnDelete_Click(object sender, System.EventArgs e)
        {
            if( !KissMsg.ShowQuestion("CtlFallLöschen", "FallWirklichLöschen", "Wollen Sie den Fall {0} und die davon abhängigen Daten wirklich definitiv und unwiderruflich aus der KiSS-DB löschen?", 0, 0, true, edtFall.LookupID) )
                return;

            try
            {
                DBUtil.ExecSQLThrowingException( "exec [spDeleteFall] {0}", edtFall.LookupID );
                qryDependentRows.Refresh();
                edtFall.EditValue = DBNull.Value;
                edtFall.LookupID  = DBNull.Value;
                KissMsg.ShowInfo("CtlFallLöschen", "FallGelöscht", "Der Fall wurde gelöscht");
            }
            catch(Exception ex)
            {
                KissMsg.ShowError("CtlFallLöschen", "FallNichtLöschbar", "Der Fall konnte nicht gelöscht werden. Wahrscheinlich bestehen noch Abhängigkeiten, die nicht automatisch gelöscht werden", ex);
            }
        }

        private void edtFall_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            string SearchString = edtFall.EditValue.ToString().Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            int? searchNr = null;
            int tmp;
            if( int.TryParse(edtFall.EditValue.ToString(), out tmp) )
                searchNr = tmp;

            if (DBUtil.IsEmpty(SearchString))
            {
                if (e.ButtonClicked)
                {
                    SearchString = "%";
                }
                else
                {
                    edtFall.EditValue = DBNull.Value;
                    edtFall.LookupID  = DBNull.Value;
                }
            }

            KissLookupDialog dlg = new KissLookupDialog();
            e.Cancel = !dlg.SearchData(@"
              SELECT FallNr              = FAL.FaFallID,
                     Fallträger          = PRS.NameVorname,
                     Wohnsitz            = PRS.Wohnsitz,
                     DisplayText$        = 'Fall ' + CAST(FAL.FaFallID AS varchar) + ' - FT ' + PRS.NameVorname
              FROM   FaFall         FAL
                INNER JOIN vwPerson PRS ON PRS.BaPersonID = FAL.BaPersonID
              WHERE  PRS.NameVorname LIKE '%' + {0} + '%'
                OR FAL.FaFallID = {1}
              ORDER BY PRS.NameVorname",
              SearchString,
              e.ButtonClicked,searchNr,null,null);

            if (!e.Cancel)
            {
                    edtFall.EditValue = dlg["DisplayText$"];
                edtFall.LookupID  = dlg["FallNr"];
                RefreshDependentRowsTable();
            }
        }

