        private void DlgFallZuteilung_Load(object sender, System.EventArgs e)
        {
            //Anzeige Modul-Icons
            repItemModulIcon.SmallImages = KissImageList.SmallImageList;

            this.repItemModulIcon.Items.Add(new DevExpress.XtraEditors.Controls.ImageComboBoxItem("", 193, KissImageList.GetImageIndex(193)));

            SqlQuery qryModul = DBUtil.OpenSQL(@"
                SELECT IconID = 100 * ModulID + 1000 FROM XModul WHERE ModulTree = 1
                UNION ALL
                SELECT 1230
                UNION ALL
                SELECT 1240");
            foreach (DataRow row in qryModul.DataTable.Rows)
            {
                for (int i = (int)row["IconID"]; i < (int)row["IconID"] + 10; i++)
                    this.repItemModulIcon.Items.Add(new DevExpress.XtraEditors.Controls.ImageComboBoxItem("", i, KissImageList.GetImageIndex(i)));
            }

            //Spezialrechte
            LTWechsel = DBUtil.UserHasRight("DlgFallZuteilung_LTWechsel");
            FTWechsel = DBUtil.UserHasRight("DlgFallZuteilung_FTWechsel");
            MAzuteilen = DBUtil.UserHasRight("DlgFallZuteilung_MAzuteilen");
            SBzuteilen = DBUtil.UserHasRight("DlgFallZuteilung_SBzuteilen");
            GeschlosseneZuteilen = DBUtil.UserHasRight("DlgFallZuteilung_GeschlosseneZuteilen");

            LTWechsel_OE = DBUtil.UserHasRight("DlgFallZuteilung_LTWechsel_OE");
            FTWechsel_OE = DBUtil.UserHasRight("DlgFallZuteilung_FTWechsel_OE");
            MAzuteilen_OE = DBUtil.UserHasRight("DlgFallZuteilung_MAzuteilen_OE");
            SBzuteilen_OE = DBUtil.UserHasRight("DlgFallZuteilung_SBzuteilen_OE");
            GeschlosseneZuteilen_OE = DBUtil.UserHasRight("DlgFallZuteilung_GeschlosseneZuteilen_OE");

            RechteAnzeigen = DBUtil.UserHasRight("DlgFallZuteilung_RechteAnzeigen");

            btnSpezialrechteAnzeigen.Visible = RechteAnzeigen;

            // alle Personen aller Klientensysteme von BaPersonID
            SqlQuery qryBetrifft = DBUtil.OpenSQL(@"
                SELECT DISTINCT
                  Code = PRS.BaPersonID,
                  Text = PRS.NameVorname
                FROM FaFall     FAL
                  INNER JOIN FaFallPerson FAP ON FAL.FaFallID = FAP.FaFAllID
                  INNER JOIN vwPerson     PRS ON PRS.BaPersonID = FAP.BaPersonID
                WHERE FAL.BaPersonID = {0}
                ORDER BY Text",
                baPersonID);

            edtBaPersonID.LoadQuery(qryBetrifft);

            grvGastzugriff.OptionsView.ShowIndicator = false;
            btnOK.BringToFront();

            tabFallZuteilung.SelectedTabIndex = 0;

            qryUser.Fill();
            colFall.GroupIndex = 0;
            qryFallzugriff.Fill(baPersonID);
            grvFallzugriff.ExpandAllGroups();

            qryFallzugriff.Find("ModulID = 2");
        }

        private void btnFTWechsel_Click(object sender, System.EventArgs e)
        {
            if ((int)qryFallzugriff["BaPersonID"] == (int)qryFallzugriff["BaPersonIDOrig"]) return;

            int prozess = (int)qryFallzugriff["FaProzessCode"];

            if (prozess == 200 || prozess == 201)
            {
                if (KissMsg.ShowQuestion("Soll der/die Fallträger/in gewechselt werden?"))
                {
                    DBUtil.ExecSQL(@"
                        IF NOT EXISTS(SELECT * FROM FaLeistung WHERE FaFallID = {1} AND FaProzessCode = 200 AND 200 <> {3})
                          UPDATE FaFall SET BaPersonID = {0} WHERE FaFallID = {1}

                        UPDATE FaLeistung SET BaPersonID = {0}, Modifier = {4}, Modified = GetDate() WHERE FaLeistungID = {2}",
                        qryFallzugriff["BaPersonID"],
                        qryFallzugriff["FaFallID"],
                        qryFallzugriff["FaLeistungID"],
                        qryFallzugriff["FaProzessCode"],
                        DBUtil.GetDBRowCreatorModifier());

                    //Eintrag ins Fallverlaufsprotokoll
                    DBUtil.ExecSQL(@"
                        INSERT FaJournal (FaFallID, FaLeistungID, BaPersonID, UserID, Text, OrgUnitID)
                          VALUES ({0}, null, null, {1}, {2}, {3})",
                        qryFallzugriff["FaFallID"],
                        Session.User.UserID,
                        "Wechsel Fallträger/in: " + edtBaPersonID.Text,
                        Session.User["OrgUnitID"]);

                    FormController.SendMessage("FrmFall", "Action", "CloseFrmFall");
                    FormController.OpenForm("FrmFall", "Action", "ShowFall", "BaPersonID", (int)qryFallzugriff["BaPersonID"], "ModulCode", ModulID.B);
                }
            }
            else
            {
                if (KissMsg.ShowQuestion("Soll der/die Leistungsträger/in gewechselt werden?"))
                {
                    DBUtil.ExecSQL("UPDATE FaLeistung SET BaPersonID = {0}, Modifier = {1}, Modified = GetDate() WHERE FaLeistungID = {2}",
                        qryFallzugriff["BaPersonID"],
                        DBUtil.GetDBRowCreatorModifier(),
                        qryFallzugriff["FaLeistungID"]);

                    //Eintrag ins Fallverlaufsprotokoll
                    DBUtil.ExecSQL(@"
                        INSERT FaJournal (FaFallID, FaLeistungID, BaPersonID, UserID, Text, OrgUnitID)
                          VALUES ({0}, {1}, null, {2}, {3}, {4})",
                        qryFallzugriff["FaFallID"],
                        qryFallzugriff["FaLeistungID"],
                        Session.User.UserID,
                        "Wechsel Leistungsträger/in: " + edtBaPersonID.Text,
                        Session.User["OrgUnitID"]);
                }
            }

            string FaLeistungID = qryFallzugriff["FaLeistungID"].ToString();
            FormController.SendMessage("FrmFallNavigator", "Action", "RefreshTree");
            qryFallzugriff.Refresh();
            grvFallzugriff.ExpandAllGroups();
            qryFallzugriff.Find("FaLeistungID = " + FaLeistungID);
        }

        private void btnGastLoeschen_Click(object sender, System.EventArgs e)
        {
            if (qryGastzugriff.Count == 0) return;

            try
            {
                // Fallverlaufseintrag wird in events erstellt
                qryGastzugriff.Delete();
            }
            catch (Exception exception)
            {
                //7861 User informieren, dass das Wegnehmen Gastrecht nicht geklappt hat.
                KissMsg.ShowError(this.GetType().Name, "ErrorRemovingGuest", "Beim Entfernen von Gastrechten ist ein Fehler aufgetreten.", exception);
            }

            if (Session.Transaction != null)
            {
                Session.Rollback();

                //7861 User informieren, dass das Wegnehmen Gastrecht nicht geklappt hat.
                KissMsg.ShowError(this.GetType().Name, "ErrorRemovingGuest", "Beim Entfernen von Gastrechten ist ein Fehler aufgetreten.");
            }

            qryFallzugriff["AnzGaeste"] = qryGastzugriff.Count;
        }

        private void btnGastNeu_Click(object sender, System.EventArgs e)
        {
            if (qryUser.Count == 0 || DBUtil.IsEmpty(qryFallzugriff["FaLeistungID"])) return;

            SqlQuery qry = DBUtil.OpenSQL(
                "select 1 from FaLeistungZugriff where FaLeistungID = {0} and UserID = {1}",
                qryFallzugriff["FaLeistungID"],
                qryUser["UserID"]);

            if (qry.Count > 0)
            {
                KissMsg.ShowInfo("Diese/r Mitarbeiter/in hat bereits Gastzugriff");
                return;
            }

            try
            {
                Session.BeginTransaction();
                qryGastzugriff.Insert();
                qryGastzugriff["FaLeistungID"] = qryFallzugriff["FaLeistungID"];
                qryGastzugriff["UserID"] = qryUser["UserID"];
                qryGastzugriff["DarfMutieren"] = false;
                qryGastzugriff["UserInfo"] = qryUser["UserInfo"];
                if (qryGastzugriff.Post())
                {
                    Session.Commit();
                }
            }
            finally
            {
                if (Session.Transaction != null)
                {
                    Session.Rollback();
                }
            }

            //Eintrag ins Fallverlaufsprotokoll
            DBUtil.ExecSQL(@"
                INSERT FaJournal (FaFallID, FaLeistungID, BaPersonID, UserID, Text, OrgUnitID)
                  VALUES ({0}, {1}, NULL, {2}, {3}, {4})",
                    qryFallzugriff["FaFallID"],
                qryFallzugriff["FaLeistungID"],
                Session.User.UserID,
                "Gastrecht hinzugefügt für " + qryGastzugriff["UserInfo"].ToString(),
                Session.User["OrgUnitID"]);

            qryFallzugriff["AnzGaeste"] = qryGastzugriff.Count;
        }

        private void btnMA_Click(object sender, System.EventArgs e)
        {
            if (qryUser.Count == 0) return;
            if (qryUser["UserID"].ToString() == qryFallzugriff["MAUserID"].ToString()) return;

            //Pendenzen umhaengen: Nur offene Pendezen mit alten SAR als Empfaenger werden umgehängt
            //prüfen, ob unerledigte Pendenzen existieren, die umgehaengt werden muessen
            var faLeistungID = qryFallzugriff["FaLeistungID"];

            if (DBUtil.GetConfigBool(@"System\Pendenzen\Pendenzenverwaltung\FragenVorWechselLV", false))
            {
                //taskstatuscode 3 = erledigt
                int countUnerledigte = (int)DBUtil.ExecuteScalarSQLThrowingException(@"
                    SELECT COUNT(*)
                    FROM dbo.XTask WITH(READUNCOMMITTED)
                    WHERE FaLeistungID = {0}
                    AND TaskStatusCode <> 3
                    AND ReceiverID = {1}",
                        faLeistungID,
                        qryFallzugriff["MAUserID"]);

                if (countUnerledigte > 0)
                {
                    //Bei Nein im Dialog -> abbrechen
                    if (!KissMsg.ShowQuestion(CLASSNAME, "DlgWechselLeistungsverantwortung", FallUtil.LV_WECHSEL_PENDENZEN_FRAGE))
                    {
                        return;
                    }
                }
            }

            // Umhaengen der Pendenzen
            DBUtil.ExecSQL(@"UPDATE dbo.XTask
                                     SET    ReceiverID = {0}
                                     WHERE  ReceiverID = {1}
                                       AND  FaLeistungID = {2}
                                       AND  TaskStatusCode <> 3 ", qryUser["UserID"], qryFallzugriff["MAUserID"], faLeistungID);


            DBUtil.ExecSQL(@"UPDATE FaLeistung SET UserID = {0}, Modifier = {1}, Modified = GetDate() WHERE FaLeistungID = {2}",
                qryUser["UserID"],
                DBUtil.GetDBRowCreatorModifier(),
                qryFallzugriff["FaLeistungID"]);

            //Eintrag ins Fallverlaufsprotokoll
            DBUtil.ExecSQL(@"
                INSERT FaJournal (FaFallID, FaLeistungID, BaPersonID, UserID, Text, OrgUnitID)
                  VALUES ({0}, {1}, NULL, {2}, {3}, {4})",
                qryFallzugriff["FaFallID"],
                qryFallzugriff["FaLeistungID"],
                Session.User.UserID,
                string.Format("Wechsel Leistungsverantwortung: von {0} zu {1}", qryFallzugriff["MaInfo"], qryUser["UserInfo"]),
                Session.User["OrgUnitID"]);

            if ((int)qryFallzugriff["ModulID"] == 2
                && (int)qryFallzugriff["FaProzessCode"] == (int)DBUtil.ExecuteScalarSQL(@"
                    SELECT TOP 1 FaProzessCode
                    FROM FaLeistung
                    WHERE FaFallID = {0} AND FaProzessCode IN (200, 201)
                    ORDER BY IsNull(DatumBis, '99991231') DESC, FaProzessCode", qryFallzugriff["FaFallID"]))
            {
                DBUtil.ExecSQL(@"UPDATE FaFall SET UserID = {0} WHERE FaFallID = {1}",
                    qryUser["UserID"],
                    qryFallzugriff["FaFallID"]);

                //Eintrag ins Fallverlaufsprotokoll
                DBUtil.ExecSQL(@"
                    INSERT FaJournal (FaFallID, FaLeistungID, BaPersonID, UserID, Text, OrgUnitID)
                      VALUES ({0}, null, null, {1}, {2}, {3})",
                    qryFallzugriff["FaFallID"],
                    Session.User.UserID,
                    string.Format("Wechsel Fallverantwortung: von {0} zu {1}", qryFallzugriff["MaInfo"], qryUser["UserInfo"]),
                    Session.User["OrgUnitID"]);

                FormController.SendMessage("FrmFallNavigator", "Action", "RefreshTree");
            }

            Refresh();
        }

