        private void qryFallzugriff_PositionChanged(object sender, System.EventArgs e)
        {
            // Update: es dürfen nur Klienten innerhalb des Klientensystems ausgewählt werden
            edtBaPersonID.LoadQuery(DBUtil.OpenSQL(@"
                SELECT DISTINCT
                  Code = PRS.BaPersonID,
                  Text = PRS.NameVorname
                FROM FaFallPerson      FAP
                  INNER JOIN vwPerson  PRS ON PRS.BaPersonID = FAP.BaPersonID
                WHERE FAP.FaFallID = {0}
                ORDER BY Text",
                qryFallzugriff["FaFallID"]));

            qryGastzugriff.Fill(qryFallzugriff["FaLeistungID"]);
            qryArchiv.Fill(qryFallzugriff["FaLeistungID"]);

            bool AllowEdit = DBUtil.IsEmpty(qryFallzugriff["DatumBis"]);

            btnMA.Enabled = (AllowEdit || DBUtil.UserHasRight("DlgFallZuteilung_GeschlosseneZuteilen")) && DBUtil.UserHasRight("DlgFallZuteilung_MAzuteilen");
            btnSB.Enabled = (AllowEdit || DBUtil.UserHasRight("DlgFallZuteilung_GeschlosseneZuteilen")) && DBUtil.UserHasRight("DlgFallZuteilung_SBzuteilen");
            btnSBLoeschen.Enabled = (AllowEdit || DBUtil.UserHasRight("DlgFallZuteilung_GeschlosseneZuteilen")) && DBUtil.UserHasRight("DlgFallZuteilung_SBzuteilen");

            btnGastNeu.Enabled = AllowEdit && !DBUtil.IsEmpty(qryFallzugriff["FaLeistungID"]) && DBUtil.UserHasRight("DlgFallZuteilung_GastZuteilen");
            btnGastLoeschen.Enabled = btnGastNeu.Enabled;

            int prozess = (int)qryFallzugriff["FaProzessCode"];

            if (AllowEdit && (prozess == 200 || prozess == 201) && DBUtil.UserHasRight("DlgFallZuteilung_FTWechsel"))
            {
                //Fallführung + Fallführung Alim
                edtBaPersonID.EditMode = EditModeType.Normal;
                btnFTWechsel.Text = "FT Wechsel";
                btnFTWechsel.Visible = true;
            }
            else if (AllowEdit && (prozess > 300 && prozess < 500) && DBUtil.UserHasRight("DlgFallZuteilung_LTWechsel"))
            {
                //alle A-Leistungen + W-Inkasso
                edtBaPersonID.EditMode = EditModeType.Normal;
                btnFTWechsel.Text = "LT Wechsel";
                btnFTWechsel.Visible = true;
            }
            else
            {
                edtBaPersonID.EditMode = EditModeType.ReadOnly;
                btnFTWechsel.Visible = false;
            }
        }

        private void qryGastzugriff_AfterDelete(object sender, EventArgs e)
        {
            Session.Commit();
        }

        private void qryGastzugriff_BeforeDelete(object sender, EventArgs e)
        {
            Session.BeginTransaction();
            //Eintrag ins Fallverlaufsprotokoll
            DBUtil.ExecSQL(@"
                INSERT FaJournal (FaFallID, FaLeistungID, BaPersonID, UserID, Text, OrgUnitID)
                  VALUES ({0}, {1}, NULL, {2}, {3}, {4})",
                    qryFallzugriff["FaFallID"],
                qryFallzugriff["FaLeistungID"],
                Session.User.UserID,
                "Gastrecht gelöscht für " + qryGastzugriff["UserInfo"].ToString(),
                Session.User["OrgUnitID"]);
        }

        private void qryGastzugriff_BeforePost(object sender, System.EventArgs e)
        {
            if (qryGastzugriff.Row.RowState == DataRowState.Modified)
            {
                //Eintrag ins Fallverlaufsprotokoll: Nur "DarfMutieren" kann sich geändert haben
                DBUtil.ExecSQL(@"
                INSERT FaJournal (FaFallID, FaLeistungID, BaPersonID, UserID, Text, OrgUnitID)
                  VALUES ({0}, {1}, null, {2}, {3}, {4})",
                        qryFallzugriff["FaFallID"],
                    qryFallzugriff["FaLeistungID"],
                    Session.User.UserID,
                    "Gastrecht verändert: Mutation " + ((bool)qryGastzugriff["DarfMutieren"] ? "" : "nicht") + " erlaubt für " + qryGastzugriff["UserInfo"].ToString(),
                    Session.User["OrgUnitID"]);
            }
        }

