        private new void Refresh()
        {
            qryGastzugriff.Post();

            object Key = qryFallzugriff["Key"];

            qryFallzugriff.Fill(baPersonID);
            grvFallzugriff.ExpandAllGroups();

            int RowHandle = grvFallzugriff.LocateByValue(-1000, colKey, Key);
            if (RowHandle != DevExpress.XtraGrid.GridControl.InvalidRowHandle)
            {
                grvFallzugriff.FocusedRowHandle = RowHandle;
            }
        }

