        public override object GetContextValue(string FieldName)
        {
            switch (FieldName.ToUpper())
            {
                case "BETRPERSONID": return DBUtil.ExecuteScalarSQL(@"
                                    select BaPersonID
                                    from   dbo.FaLeistung WITH(READUNCOMMITTED)
                                    where  FaLeistungID = {0}",
                                       qryFaAbmachung["FaLeistungID"]);
                case "FAFALLID": return DBUtil.ExecuteScalarSQL(@"
                                    select FaFallID
                                    from   dbo.FaLeistung WITH(READUNCOMMITTED)
                                    where  FaLeistungID = {0}",
                                       qryFaAbmachung["FaLeistungID"]);
                case "FAABMACHUNGID": return qryFaAbmachung["FaAbmachungID"];
                case "ABSENDER": return 0;
            }
            return base.GetContextValue(FieldName);
        }

        public void Init(string Name, Image TitleImage, int LeistungID, int FallID)
        {
            picTitel.Image = TitleImage;
            faLeistungID = LeistungID;
            faFallID = FallID;

            // Auswahl Klienten:
            // alle Personen im Klientensystem von BaPersonID
            SqlQuery qryK = DBUtil.OpenSQL(@"
              SELECT Code = PRS.BaPersonID, Text = IsNull(PRS.Vorname+ ' ','') + PRS.Name
              FROM dbo.FaFallPerson FFP WITH(READUNCOMMITTED)
              LEFT JOIN dbo.BaPerson PRS WITH(READUNCOMMITTED) on PRS.BaPersonID = FFP.BaPersonID
              WHERE FFP.FaFallID = {0}
              ORDER BY 2",
              faFallID
            );
            edtKlient1.Properties.DataSource = qryK;

            // 26.11.2008 : sozheo : Person 2 soll gelöscht werden können
            qryK = DBUtil.OpenSQL(@"
              SELECT Code = NULL, Text = NULL
              UNION ALL
              SELECT Code = PRS.BaPersonID, Text = IsNull(PRS.Vorname+ ' ','') + PRS.Name
              FROM dbo.FaFallPerson FFP WITH(READUNCOMMITTED)
              LEFT JOIN dbo.BaPerson PRS WITH(READUNCOMMITTED) on PRS.BaPersonID = FFP.BaPersonID
              WHERE FFP.FaFallID = {0}
              ORDER BY 2",
              faFallID
            );
            edtKlient2.Properties.DataSource = qryK;

            //colKlient1.ColumnEdit = grdAbmachung.GetLOVLookUpEdit(qryK);
            this.colFaAbmachungSanktionen.ColumnEdit = this.grdAbmachung.GetLOVLookUpEdit((SqlQuery)this.edtFaAbmachungSanktionenCode.Properties.DataSource);

            // Daten öffnen:
            qryFaAbmachung.Fill(faLeistungID);
        }

