        // sozksc: regeln wie definiert umgesetzt, auf Schönheit verzichtet, da sie sowieso wieder ändern werden...
        private void SetEditMode()
        {
            EditModeType editMode = EditModeType.ReadOnly;

            edtEKBeschluss.EditMode = editMode;
            edtFaAbmachungSanktionenCode.EditMode = editMode;
            edtZulagen.EditMode = editMode;
            edtGBL.EditMode = editMode;
            edtSanktionAb.EditMode = editMode;
            edtSanktionBis.EditMode = editMode;

            if (edtAuflageErfuellt.EditValue is int)
            {
                editMode = (int)edtAuflageErfuellt.EditValue == 0 ? EditModeType.ReadOnly : EditModeType.Normal;
                edtEKBeschluss.EditMode = editMode;
                edtFaAbmachungSanktionenCode.EditMode = editMode;
                edtZulagen.EditMode = editMode;
                edtGBL.EditMode = editMode;
                edtSanktionAb.EditMode = editMode;
                edtSanktionBis.EditMode = editMode;

                if (editMode == EditModeType.ReadOnly)
                {
                    edtEKBeschluss.EditValue = null;
                    edtFaAbmachungSanktionenCode.EditValue = null;
                    edtZulagen.EditValue = null;
                    edtGBL.EditValue = null;
                    edtSanktionAb.EditValue = null;
                    edtSanktionBis.EditValue = null;
                }
            }
            if (edtFaAbmachungSanktionenCode.EditValue is int)
            {
                // Regel 2
                if ((int)edtFaAbmachungSanktionenCode.EditValue == 1)
                {
                    edtEKBeschluss.EditMode = EditModeType.Normal;
                    edtFaAbmachungSanktionenCode.EditMode = EditModeType.Normal;
                    edtZulagen.EditMode = EditModeType.Normal;
                    edtGBL.EditMode = EditModeType.Normal;
                    edtSanktionAb.EditMode = EditModeType.Normal;
                    edtSanktionBis.EditMode = EditModeType.Normal;
                }
                // Regel 3
                else
                {
                    edtSanktionAb.EditMode = EditModeType.Normal;
                    edtEKBeschluss.EditMode = EditModeType.Normal;
                    edtFaAbmachungSanktionenCode.EditMode = EditModeType.Normal;
                    edtZulagen.EditMode = EditModeType.ReadOnly;
                    edtGBL.EditMode = EditModeType.ReadOnly;
                    edtSanktionAb.EditMode = EditModeType.Normal;
                    edtSanktionBis.EditMode = EditModeType.ReadOnly;
                }
            }
        }

