        private void qryFaAbmachung_BeforePost(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 21.08.2007 : sozheo : Alles neu
            // 12.12.2007 : sozksc : bereinigt; angepasst nach Mantis 1140; neue Felder/andere weg (Totalüberarbeitung)
            // ------------------------------------------------------------------------

            DBUtil.CheckNotNullField(edtBetreff, lblBetreff.Text);
            DBUtil.CheckNotNullField(edtKlient1, lblFuer.Text);

            if (qryFaAbmachung.Row.RowState == DataRowState.Added)
            {
                // Bei neuen Zeilen:
                qryFaAbmachung["ErstelltUserID"] = Session.User.UserID;
                qryFaAbmachung["ErstelltDatum"] = DateTime.Now;
            }
            qryFaAbmachung["MutiertUserID"] = Session.User.UserID;
            qryFaAbmachung["MutiertDatum"] = DateTime.Now;

            if (qryFaAbmachung.ColumnModified("AuflageErfuelltCode")
              && (qryFaAbmachung.Row.RowState != DataRowState.Added || !DBUtil.IsEmpty(edtAuflageErfuellt.EditValue)))
            {
                if (DBUtil.IsEmpty(qryFaAbmachung["AuflageErstelltUserID"]))
                {
                    qryFaAbmachung["AuflageErstelltUserID"] = Session.User.UserID;
                    qryFaAbmachung["AuflageErstelltDatum"] = DateTime.Now;
                }

                qryFaAbmachung["AuflageMutiertUserID"] = Session.User.UserID;
                qryFaAbmachung["AuflageMutiertDatum"] = DateTime.Now;
            }
        }

