        #region Public Methods

        public override object GetContextValue(string FieldName)
        {
            switch (FieldName.ToUpper())
              {
            case "BETRPERSONID" : return DBUtil.ExecuteScalarSQL(@"
                                    select BaPersonID
                                    from   FaLeistung
                                    where  FaLeistungID = {0}",
                                    qryFaAssessment["FaLeistungID"]);
            case "FALLUSERID"   : return DBUtil.ExecuteScalarSQL(@"
                                    select FAL.UserID
                                    from   FaFall FAL
                                           inner join FaLeistung LEI on LEI.FaFallID = FAL.FaFallID
                                    where LEI.FaLeistungID = {0}",
                                    qryFaAssessment["FaLeistungID"]);
            case "FAFALLID"  	: return DBUtil.ExecuteScalarSQL(@"
                                    select FaFallID
                                    from   FaLeistung
                                    where  FaLeistungID = {0}",
                                    qryFaAssessment["FaLeistungID"]);
            case "ABSENDER"     : return 0;
              }
              return base.GetContextValue(FieldName);
        }

        public void Init(string Name, Image TitleImage, int LeistungID, int ID)
        {
            lblTitel.Text = Name;
              picTitel.Image = TitleImage;
              faLeistungID = LeistungID;
              faAssessmentID = ID;

              // Daten anzeigen:
              qryFaAssessment.Fill(faAssessmentID);

              edtMitarbeiter.ToolTip = Utils.ConvertToString(qryFaAssessment["BerichtUser"]);
        }

        #endregion

        #region Private Methods

        private void edtMitarbeiter_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            // Letzte Bearbeitung
            // 15.12.2007 : sozheo : Neues SQL für Auswahl Mitarbeiter
            // ------------------------------------------------------------------

            string SearchString = Utils.ConvertToString(edtMitarbeiter.EditValue).ToString().Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            if (DBUtil.IsEmpty(SearchString))
            {
              if (e.ButtonClicked)
                SearchString = "%";
              else
              {
                qryFaAssessment["BerichtUserID"]  = DBNull.Value;
                qryFaAssessment["BerichtUser"] = DBNull.Value;
                edtMitarbeiter.ToolTip = "";
                return;
              }
            }

            KissLookupDialog dlg = new KissLookupDialog();
            e.Cancel = !dlg.SearchData(@"
              SELECT ID$ = UserID,
                     [Kürzel] = LogonName,
                     [Mitarbeiter/in] = NameVorname,
                     [Org.Einheit] = OrgUnit,
                 LogonNameVornameOrgUnit$ = LogonNameVornameOrgUnit
              FROM     vwUser
              WHERE    LogonNameVornameOrgUnit like '%' + {0} + '%'
              ORDER BY NameVorname",
              SearchString,
              e.ButtonClicked,null,null,null);

            if (!e.Cancel)
            {
              qryFaAssessment["BerichtUserID"] = dlg[0];
              qryFaAssessment["BerichtUser"] = dlg[4];
              edtMitarbeiter.ToolTip   = Utils.ConvertToString(dlg[4]);
            }
        }

        #endregion
