        public override object GetContextValue(string FieldName)
        {
            switch (FieldName.ToUpper())
            {
                case "BETRPERSONID": return DBUtil.ExecuteScalarSQL(@"
                                    select BaPersonID
                                    from   FaLeistung
                                    where  FaLeistungID = {0}",
                                       qryFaCheckIn["FaLeistungID"]);
                case "FALLUSERID": return DBUtil.ExecuteScalarSQL(@"
                                    select FAL.UserID
                                    from   FaFall FAL
                                           inner join FaLeistung LEI on LEI.FaFallID = FAL.FaFallID
                                    where LEI.FaLeistungID = {0}",
                                     qryFaCheckIn["FaLeistungID"]);
                case "FAFALLID": return DBUtil.ExecuteScalarSQL(@"
                                    select FaFallID
                                    from   FaLeistung
                                    where  FaLeistungID = {0}",
                                   qryFaCheckIn["FaLeistungID"]);
                case "ABSENDER": return 0; 
            }
            return base.GetContextValue(FieldName);
        }

        public void Init(string Name, Image TitleImage, int LeistungID, int ID, int PersonID)
        {
            lblTitelCheckIn.Text = Name;
            picTitel.Image = TitleImage;
            faLeistungID = LeistungID;
            faCheckinID = ID;
            BaPersonID = PersonID;

            // User-Anzeige holen
            SqlQuery qry = DBUtil.OpenSQL(@"
            select LogonNameVornameOrgUnit, OrgUnit, OrgUnitID from vwUser
            where UserID = {0} ",
            Session.User.UserID);
            UserWholeName = Utils.ConvertToString(qry["LogonNameVornameOrgUnit"]);
            UserQTID = Utils.ConvertToInt(qry["OrgUnitID"]);
            UserQTName = Utils.ConvertToString(qry["OrgUnit"]);

            // Checkbox gemäss Akgtennotizen setzen
            SqlQuery qryAkt = DBUtil.OpenSQL(@"
            select count(*) as Anzahl from FaAktennotizen
            where FaLeistungID = {0}
            and FaGespraechstypCode=1 ",
            faLeistungID);
            int Anzahl = Utils.ConvertToInt(qryAkt["Anzahl"]);
            chkGespraeche.Checked = (Anzahl > 0);
            string tmp = "1 Aktennotiz)";
            if (Anzahl > 1)
            {
                tmp = Anzahl.ToString() + " Aktennotizen)";
            }
            chkGespraeche.Text = "Es wurden Check-in-Gespräche mit SA durchgeführt (siehe Aktennotizen, " +  tmp;

            // Spezialrechte für Weiterleitung an Stellenleitung
            HasSpecialRights = DBUtil.UserHasRight("CtlFaCheckin_WeitergabeAnStellenleiter");

            // Daten öffnen:
            qryFaCheckIn.Fill(faCheckinID);
        }

